contracts/BulkPayroll.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title BulkPayroll
 * @dev Enhanced bulk payment contract with failure isolation
 * @notice Handles bulk ETH and ERC20 transfers with individual failure handling
 */
contract BulkPayroll is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    constructor() Ownable(msg.sender) {}

    /// @dev Maximum recipients per batch to prevent gas limit issues
    uint256 public constant MAX_RECIPIENTS = 200;

    /// @dev Gas limit for ETH transfers to prevent griefing
    uint256 public constant GAS_LIMIT = 50000;

    /// @dev Mapping for failed transfers: recipient => token => amount
    mapping(address => mapping(address => uint256)) public failedTransfers;

    /// @dev Custom errors
    error LengthMismatch();
    error InsufficientBalance();
    error InsufficientAllowance();
    error TransferError();
    error RefundFailed();
    error TooManyRecipients();
    error InvalidInput();

    /// @dev Events
    event BulkTransferExecuted(
        address indexed sender,
        address indexed token,
        uint256 totalRecipients,
        uint256 totalAmount,
        uint256 successCount,
        uint256 failCount
    );

    event TransferFailed(
        address indexed recipient,
        address indexed token,
        uint256 amount
    );

    event FailedTransferClaimed(
        address indexed recipient,
        address token,
        uint256 amount
    );

    /**
     * @notice Executes a bulk transfer of tokens/Native currency with failure isolation
     * @dev Failed transfers are stored for later claiming
     * @param token ERC20 token address or address(0) for Native currency
     * @param recipients Array of recipient addresses
     * @param amounts Array of corresponding transfer amounts
     */
    function bulkTransfer(
        address token,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) external payable nonReentrant {
        uint256 length = recipients.length;

        // Input validation
        if (length == 0) revert InvalidInput();
        if (length > MAX_RECIPIENTS) revert TooManyRecipients();
        if (length != amounts.length) revert LengthMismatch();

        uint256 totalAmount;
        for (uint256 i; i < length;) {
            if (recipients[i] == address(0) || amounts[i] == 0) revert InvalidInput();
            totalAmount += amounts[i];
            unchecked { ++i; }
        }

        if (token == address(0)) {
            _handleNativeTransfer(totalAmount, recipients, amounts);
        } else {
            _handleERC20Transfer(token, totalAmount, recipients, amounts);
        }
    }

    /**
     * @notice Claim failed transfers
     * @param token Token to claim (address(0) for ETH)
     */
    function claimFailedTransfer(address token) external nonReentrant {
        uint256 amount = failedTransfers[msg.sender][token];
        if (amount == 0) revert InvalidInput();

        failedTransfers[msg.sender][token] = 0;

        bool success = _attemptTransfer(msg.sender, token, amount);

        if (success) {
            emit FailedTransferClaimed(msg.sender, token, amount);
        } else {
            failedTransfers[msg.sender][token] = amount; // Restore balance
            revert TransferError();
        }
    }

    /**
     * @dev Handle native currency transfers with failure isolation
     */
    function _handleNativeTransfer(
        uint256 totalAmount,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) private {
        if (msg.value < totalAmount) revert InsufficientBalance();

        uint256 successCount = 0;
        uint256 failCount = 0;

        for (uint256 i; i < recipients.length;) {
            if (_attemptTransfer(recipients[i], address(0), amounts[i])) {
                successCount++;
            } else {
                failCount++;
                failedTransfers[recipients[i]][address(0)] += amounts[i];
                emit TransferFailed(recipients[i], address(0), amounts[i]);
            }
            unchecked { ++i; }
        }

        emit BulkTransferExecuted(msg.sender, address(0), recipients.length, totalAmount, successCount, failCount);
        _refundExcessNative(totalAmount);
    }

    /**
     * @dev Handle ERC20 transfers with failure isolation
     */
    function _handleERC20Transfer(
        address token,
        uint256 totalAmount,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) private {
        IERC20 tokenContract = IERC20(token);
        uint256 allowance = tokenContract.allowance(msg.sender, address(this));
        if (allowance < totalAmount) revert InsufficientAllowance();

        // Transfer total amount to contract first
        tokenContract.safeTransferFrom(msg.sender, address(this), totalAmount);

        uint256 successCount = 0;
        uint256 failCount = 0;

        for (uint256 i; i < recipients.length;) {
            if (_attemptTransfer(recipients[i], token, amounts[i])) {
                successCount++;
            } else {
                failCount++;
                failedTransfers[recipients[i]][token] += amounts[i];
                emit TransferFailed(recipients[i], token, amounts[i]);
            }
            unchecked { ++i; }
        }

        emit BulkTransferExecuted(msg.sender, token, recipients.length, totalAmount, successCount, failCount);
    }

    /**
     * @dev Attempt transfer with gas limit protection
     */
    function _attemptTransfer(
        address recipient,
        address token,
        uint256 amount
    ) internal returns (bool success) {
        if (token == address(0)) {
            (success, ) = recipient.call{value: amount, gas: GAS_LIMIT}("");
        } else {
            try IERC20(token).transfer(recipient, amount) {
                success = true;
            } catch {
                success = false;
            }
        }
    }

    /**
     * @dev Refund excess native currency
     */
    function _refundExcessNative(uint256 totalAmount) private {
        uint256 remainder = msg.value - totalAmount;
        if (remainder > 0) {
            (bool success,) = msg.sender.call{value: remainder, gas: GAS_LIMIT}("");
            if (!success) revert RefundFailed();
        }
    }

    /**
     * @notice Get failed transfer amount for a recipient
     */
    function getFailedAmount(address recipient, address token) external view returns (uint256) {
        return failedTransfers[recipient][token];
    }

    /**
     * @notice Emergency withdrawal for owner (only failed transfers)
     */
    function emergencyWithdraw(address token) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(address(this).balance);
        } else {
            IERC20(token).safeTransfer(owner(), IERC20(token).balanceOf(address(this)));
        }
    }

    // Accept ETH
    receive() external payable {}
}

contracts/DummyUSDT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title DummyUSDT
 * @dev A simple ERC20 token for testing purposes with faucet functionality
 * @notice Compatible with Kaia testnet, includes built-in faucet for easy testing
 */
contract DummyUSDT is ERC20, Ownable {

    /// @dev Faucet configuration
    uint256 public constant FAUCET_AMOUNT = 1000 * 10**18; // 1000 tokens
    uint256 public constant FAUCET_COOLDOWN = 24 hours;

    /// @dev Mapping to track last faucet claim time
    mapping(address => uint256) public lastFaucetClaim;

    /// @dev Events
    event FaucetClaimed(address indexed user, uint256 amount);
    event FaucetFunded(uint256 amount);

    /// @dev Custom errors
    error FaucetCooldown();
    error FaucetEmpty();

    /**
     * @dev Constructor - creates initial supply and sets up faucet
     * @param initialSupply Initial token supply (without decimals)
     */
    constructor(uint256 initialSupply) ERC20("Dummy USDT", "DUSDT") Ownable(msg.sender) {
        // Mint initial supply to owner
        _mint(msg.sender, initialSupply * 10**decimals());

        // Fund faucet with 10% of initial supply
        uint256 faucetFunding = (initialSupply * 10**decimals()) / 10;
        _mint(address(this), faucetFunding);

        emit FaucetFunded(faucetFunding);
    }

    /**
     * @notice Claim tokens from faucet (once per 24 hours)
     * @dev Anyone can call this to get test tokens
     */
    function faucet() external {
        // Check cooldown
        if (block.timestamp < lastFaucetClaim[msg.sender] + FAUCET_COOLDOWN) {
            revert FaucetCooldown();
        }

        // Check faucet balance
        if (balanceOf(address(this)) < FAUCET_AMOUNT) {
            revert FaucetEmpty();
        }

        // Update claim time before transfer (reentrancy protection)
        lastFaucetClaim[msg.sender] = block.timestamp;

        // Transfer tokens
        _transfer(address(this), msg.sender, FAUCET_AMOUNT);

        emit FaucetClaimed(msg.sender, FAUCET_AMOUNT);
    }

    /**
     * @notice Mint new tokens (only owner)
     * @param to Address to mint tokens to
     * @param amount Amount to mint (with decimals)
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @notice Fund the faucet (only owner)
     * @param amount Amount to fund faucet with
     */
    function fundFaucet(uint256 amount) external onlyOwner {
        _transfer(msg.sender, address(this), amount);
        emit FaucetFunded(amount);
    }

    /**
     * @notice Get faucet balance
     */
    function getFaucetBalance() external view returns (uint256) {
        return balanceOf(address(this));
    }

    /**
     * @notice Get time until next faucet claim for user
     * @param user Address to check
     */
    function getTimeUntilNextClaim(address user) external view returns (uint256) {
        uint256 nextClaim = lastFaucetClaim[user] + FAUCET_COOLDOWN;
        if (block.timestamp >= nextClaim) {
            return 0;
        }
        return nextClaim - block.timestamp;
    }

    /**
     * @notice Check if user can claim from faucet
     * @param user Address to check
     */
    function canClaimFaucet(address user) external view returns (bool) {
        return block.timestamp >= lastFaucetClaim[user] + FAUCET_COOLDOWN 
               && balanceOf(address(this)) >= FAUCET_AMOUNT;
    }

    /**
     * @notice Emergency withdrawal of faucet funds (only owner)
     */
    function withdrawFaucetFunds() external onlyOwner {
        uint256 balance = balanceOf(address(this));
        if (balance > 0) {
            _transfer(address(this), owner(), balance);
        }
    }
}

contracts/IFeedRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IFeedRouter {
    /**
     * @notice Get the latest round data of the feed given a feed name.
     * @param feedName The feed name.
     * @return id The round ID.
     * @return answer The oracle answer.
     * @return updatedAt Timestamp of the last update.
     */
    function latestRoundData(string calldata feedName)
        external
        view
        returns (uint64 id, int256 answer, uint256 updatedAt);

    /**
     * @notice Get decimals of the feed given a feed name.
     * @param feedName The feed name.
     * @return decimals The decimals of the feed.
     */
    function decimals(string calldata feedName) external view returns (uint8);

    /**
     * @notice Get supported feed names.
     * @return The feed names.
     */
    function getFeedNames() external view returns (string[] memory);
}

contracts/InvoiceSubscription.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract InvoiceSubscriptionService is ReentrancyGuard, Ownable {

    // ===================== STATE VARIABLES =====================

    uint256 private _serviceIds;
    uint256 private _invoiceIds;
    // Platform fee (in basis points, e.g., 250 = 2.5%)
    uint256 public platformFeeRate = 250;
    address public feeCollector;

    // ===================== STRUCTS =====================

    struct ServiceListing {
        uint256 serviceId;
        address seller;
        string serviceName;
        string description;
        string imageUrl;
        uint256 priceInWei;
        address paymentToken; // ERC20 token address (address(0) for ETH)
        bool isActive;
        uint256 createdAt;
    }

    struct Invoice {
        uint256 invoiceId;
        uint256 serviceId;
        address seller;
        address buyer;
        string invoiceNumber; // Format: "INV-2024-001"
        uint256 issueDate;
        uint256 dueDate;
        uint256 subtotal;
        uint256 discount; // Discount amount
        uint256 platformFee;
        uint256 totalAmount;
        address paymentToken;
        InvoiceStatus status;
        string couponCode; // Generated coupon for buyer
        bool allowsSplitPayment;
        uint256 createdAt;
    }

    struct SplitPayment {
        uint256 invoiceId;
        address[] contributors;
        mapping(address => uint256) contributions;
        mapping(address => bool) hasPaid;
        uint256 totalCollected;
        uint256 contributorCount;
    }

    enum InvoiceStatus {
        PENDING,
        PARTIALLY_PAID,
        FULLY_PAID,
        OVERDUE,
        CANCELLED
    }

    // ===================== MAPPINGS =====================

    mapping(uint256 => ServiceListing) public services;
    mapping(uint256 => Invoice) public invoices;
    mapping(uint256 => SplitPayment) public splitPayments;
    mapping(string => bool) public usedCouponCodes;
    mapping(address => uint256[]) public sellerServices;
    mapping(address => uint256[]) public buyerInvoices;

    // ===================== EVENTS =====================

    event ServiceListed(
        uint256 indexed serviceId,
        address indexed seller,
        string serviceName,
        uint256 price,
        address paymentToken
    );

    event InvoiceCreated(
        uint256 indexed invoiceId,
        uint256 indexed serviceId,
        address indexed buyer,
        address seller,
        uint256 totalAmount,
        string couponCode
    );

    event InvoicePayment(
        uint256 indexed invoiceId,
        address indexed payer,
        uint256 amount,
        InvoiceStatus newStatus
    );

    event SplitPaymentContribution(
        uint256 indexed invoiceId,
        address indexed contributor,
        uint256 amount
    );

    // ===================== CONSTRUCTOR =====================

    constructor(address _feeCollector) Ownable(msg.sender) {
        feeCollector = _feeCollector;
    }

    // ===================== SERVICE LISTING FUNCTIONS =====================

    function listService(
        string memory _serviceName,
        string memory _description,
        string memory _imageUrl,
        uint256 _priceInWei,
        address _paymentToken
    ) external returns (uint256) {
        require(bytes(_serviceName).length > 0, "Service name required");
        require(_priceInWei > 0, "Price must be greater than 0");

        _serviceIds++;
        uint256 newServiceId = _serviceIds;

        services[newServiceId] = ServiceListing({
            serviceId: newServiceId,
            seller: msg.sender,
            serviceName: _serviceName,
            description: _description,
            imageUrl: _imageUrl,
            priceInWei: _priceInWei,
            paymentToken: _paymentToken,
            isActive: true,
            createdAt: block.timestamp
        });

        sellerServices[msg.sender].push(newServiceId);

        emit ServiceListed(newServiceId, msg.sender, _serviceName, _priceInWei, _paymentToken);
        return newServiceId;
    }

    function updateServiceStatus(uint256 _serviceId, bool _isActive) external {
        require(services[_serviceId].seller == msg.sender, "Only seller can update");
        services[_serviceId].isActive = _isActive;
    }

    // ===================== INVOICE CREATION =====================

    function purchaseService(
        uint256 _serviceId,
        bool _allowSplitPayment,
        uint256 _daysUntilDue
    ) external returns (uint256) {
        ServiceListing memory service = services[_serviceId];
        require(service.isActive, "Service not active");
        require(service.seller != msg.sender, "Cannot buy own service");
        require(_daysUntilDue >= 1 && _daysUntilDue <= 90, "Due date must be 1-90 days");

        _invoiceIds++;
        uint256 newInvoiceId = _invoiceIds;
        uint256 platformFee = (service.priceInWei * platformFeeRate) / 10000;
        uint256 totalAmount = service.priceInWei + platformFee;

        // Generate unique invoice number
        string memory invoiceNumber = string(
            abi.encodePacked("INV-", _toString(block.timestamp / 86400), "-", _toString(newInvoiceId))
        );

        // Generate coupon code
        string memory couponCode = string(
            abi.encodePacked("COUPON-", _toString(newInvoiceId), "-", _toString(block.timestamp % 10000))
        );

        invoices[newInvoiceId] = Invoice({
            invoiceId: newInvoiceId,
            serviceId: _serviceId,
            seller: service.seller,
            buyer: msg.sender,
            invoiceNumber: invoiceNumber,
            issueDate: block.timestamp,
            dueDate: block.timestamp + (_daysUntilDue * 86400),
            subtotal: service.priceInWei,
            discount: 0,
            platformFee: platformFee,
            totalAmount: totalAmount,
            paymentToken: service.paymentToken,
            status: InvoiceStatus.PENDING,
            couponCode: couponCode,
            allowsSplitPayment: _allowSplitPayment,
            createdAt: block.timestamp
        });

        buyerInvoices[msg.sender].push(newInvoiceId);
        usedCouponCodes[couponCode] = true;

        // Initialize split payment if allowed
        if (_allowSplitPayment) {
            splitPayments[newInvoiceId].invoiceId = newInvoiceId;
        }

        emit InvoiceCreated(newInvoiceId, _serviceId, msg.sender, service.seller, totalAmount, couponCode);
        return newInvoiceId;
    }

    // ===================== PAYMENT FUNCTIONS =====================

    function payInvoice(uint256 _invoiceId) external payable nonReentrant {
        Invoice storage invoice = invoices[_invoiceId];
        require(invoice.status == InvoiceStatus.PENDING, "Invoice not pending");
        require(block.timestamp <= invoice.dueDate, "Invoice overdue");

        uint256 amountToPay = invoice.totalAmount;

        if (invoice.paymentToken == address(0)) {
            // ETH payment
            require(msg.value >= amountToPay, "Insufficient ETH");

            // Transfer to seller and fee collector
            payable(invoice.seller).transfer(invoice.subtotal - invoice.discount);
            payable(feeCollector).transfer(invoice.platformFee);

            // Refund excess
            if (msg.value > amountToPay) {
                payable(msg.sender).transfer(msg.value - amountToPay);
            }
        } else {
            // ERC20 payment
            IERC20 token = IERC20(invoice.paymentToken);
            require(token.transferFrom(msg.sender, invoice.seller, invoice.subtotal - invoice.discount), "Transfer to seller failed");
            require(token.transferFrom(msg.sender, feeCollector, invoice.platformFee), "Fee transfer failed");
        }

        invoice.status = InvoiceStatus.FULLY_PAID;

        emit InvoicePayment(_invoiceId, msg.sender, amountToPay, InvoiceStatus.FULLY_PAID);
    }

    // ===================== SPLIT PAYMENT FUNCTIONS =====================

    function contributeToSplitPayment(uint256 _invoiceId, uint256 _amount) external payable nonReentrant {
        Invoice storage invoice = invoices[_invoiceId];
        require(invoice.allowsSplitPayment, "Split payment not allowed");
        require(invoice.status == InvoiceStatus.PENDING || invoice.status == InvoiceStatus.PARTIALLY_PAID, "Invalid status");
        require(block.timestamp <= invoice.dueDate, "Invoice overdue");

        SplitPayment storage splitPayment = splitPayments[_invoiceId];
        require(!splitPayment.hasPaid[msg.sender], "Already contributed");

        uint256 contributionAmount;

        if (invoice.paymentToken == address(0)) {
            contributionAmount = msg.value;
            require(contributionAmount > 0, "Must send ETH");
        } else {
            contributionAmount = _amount;
            require(contributionAmount > 0, "Amount must be > 0");
            IERC20(invoice.paymentToken).transferFrom(msg.sender, address(this), contributionAmount);
        }

        // Update split payment tracking
        if (splitPayment.contributions[msg.sender] == 0) {
            splitPayment.contributors.push(msg.sender);
            splitPayment.contributorCount++;
        }

        splitPayment.contributions[msg.sender] += contributionAmount;
        splitPayment.totalCollected += contributionAmount;
        splitPayment.hasPaid[msg.sender] = true;

        // Check if invoice is fully paid
        if (splitPayment.totalCollected >= invoice.totalAmount) {
            invoice.status = InvoiceStatus.FULLY_PAID;

            // Transfer payments
            if (invoice.paymentToken == address(0)) {
                payable(invoice.seller).transfer(invoice.subtotal - invoice.discount);
                payable(feeCollector).transfer(invoice.platformFee);

                // Refund excess if any
                if (splitPayment.totalCollected > invoice.totalAmount) {
                    payable(msg.sender).transfer(splitPayment.totalCollected - invoice.totalAmount);
                }
            } else {
                IERC20 token = IERC20(invoice.paymentToken);
                token.transfer(invoice.seller, invoice.subtotal - invoice.discount);
                token.transfer(feeCollector, invoice.platformFee);
            }

            emit InvoicePayment(_invoiceId, msg.sender, contributionAmount, InvoiceStatus.FULLY_PAID);
        } else {
            invoice.status = InvoiceStatus.PARTIALLY_PAID;
            emit InvoicePayment(_invoiceId, msg.sender, contributionAmount, InvoiceStatus.PARTIALLY_PAID);
        }

        emit SplitPaymentContribution(_invoiceId, msg.sender, contributionAmount);
    }

    // ===================== COUPON FUNCTIONS =====================

    function applyCouponDiscount(uint256 _invoiceId, uint256 _discountAmount) external {
        Invoice storage invoice = invoices[_invoiceId];
        require(invoice.seller == msg.sender, "Only seller can apply discount");
        require(invoice.status == InvoiceStatus.PENDING, "Can only discount pending invoices");
        require(_discountAmount <= invoice.subtotal, "Discount too large");

        invoice.discount = _discountAmount;
        invoice.totalAmount = invoice.subtotal - _discountAmount + invoice.platformFee;
    }

    // ===================== VIEW FUNCTIONS =====================

    function getService(uint256 _serviceId) external view returns (ServiceListing memory) {
        return services[_serviceId];
    }

    function getInvoice(uint256 _invoiceId) external view returns (Invoice memory) {
        return invoices[_invoiceId];
    }

    function getSellerServices(address _seller) external view returns (uint256[] memory) {
        return sellerServices[_seller];
    }

    function getBuyerInvoices(address _buyer) external view returns (uint256[] memory) {
        return buyerInvoices[_buyer];
    }

    function getSplitPaymentDetails(uint256 _invoiceId) external view returns (
        address[] memory contributors,
        uint256 totalCollected,
        uint256 contributorCount
    ) {
        SplitPayment storage splitPayment = splitPayments[_invoiceId];
        return (splitPayment.contributors, splitPayment.totalCollected, splitPayment.contributorCount);
    }

    // ===================== ADMIN FUNCTIONS =====================

    function updatePlatformFee(uint256 _newFeeRate) external onlyOwner {
        require(_newFeeRate <= 1000, "Fee cannot exceed 10%"); // Max 10%
        platformFeeRate = _newFeeRate;
    }

    function updateFeeCollector(address _newFeeCollector) external onlyOwner {
        require(_newFeeCollector != address(0), "Invalid address");
        feeCollector = _newFeeCollector;
    }

    function markInvoiceOverdue(uint256 _invoiceId) external {
        Invoice storage invoice = invoices[_invoiceId];
        require(block.timestamp > invoice.dueDate, "Not yet overdue");
        require(invoice.status == InvoiceStatus.PENDING || invoice.status == InvoiceStatus.PARTIALLY_PAID, "Invalid status");

        invoice.status = InvoiceStatus.OVERDUE;
    }

    // ===================== UTILITY FUNCTIONS =====================

    function _toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

contracts/lendborrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./IFeedRouter.sol";

interface ILPToken is IERC20 {
    function mint(address to, uint256 amount) external;
    function burn(address from, uint256 amount) external;
}

interface IDummyUSDT is IERC20 {
    function decimals() external view returns (uint8);
}

contract LendingProtocol is ReentrancyGuard, Ownable {
    // State variables
    mapping(address => uint256) public collateralBalance;
    mapping(address => mapping(address => uint256)) public debtBalance; // borrower => token => amount
    mapping(address => uint256) public lastInterestUpdate; // borrower => timestamp

    // Token addresses
    address public immutable KAIA;
    address public immutable USDT;
    address public immutable USDY;
    address public immutable kKAIA;
    address public immutable kUSDT;

    // Oracle and price feed
    IFeedRouter public immutable feedRouter;
    string public constant KAIA_FEED_NAME = "KLAY-USDT"; // Assuming KAIA/USDT feed exists

    // Constants
    uint256 private constant PRECISION = 1e18;
    uint256 private constant MAX_LTV = 80; // 80%
    uint256 private constant LIQUIDATION_PENALTY = 10; // 10%
    uint256 private constant ANNUAL_INTEREST_RATE = 5; // 5% APR
    uint256 private constant SECONDS_PER_YEAR = 365 days;
    uint256 private constant PRICE_STALENESS_THRESHOLD = 1 hours; // Maximum age for price data

    // Bonding curve parameters
    uint256 private constant SLOPE_A = 1e12; // 0.000001 in 1e18 precision
    uint256 private constant BASE_PRICE_B = 1e18; // 1.0 in 1e18 precision

    // Stable prices (in USD with 1e18 precision)
    uint256 private constant USDT_COLLATERAL_PRICE = 1e18; // $1 (stable)

    // Custom errors
    error StalePriceData();
    error InvalidPriceData();
    error OraclePriceFeedError();

    // Events
    event Deposit(address indexed user, address indexed token, uint256 amount, uint256 lpTokens);
    event Redeem(address indexed user, address indexed lpToken, uint256 amount, uint256 underlying);
    event Borrow(address indexed user, address indexed token, uint256 amount);
    event Repay(address indexed user, address indexed token, uint256 amount);
    event CollateralDeposited(address indexed user, uint256 amount);
    event CollateralWithdrawn(address indexed user, uint256 amount);
    event Liquidate(address indexed liquidator, address indexed borrower, uint256 repayAmount, uint256 collateralSeized);

    constructor(
        address _kaia,
        address _usdt,
        address _usdy,
        address _kkaia,
        address _kusdt,
        address _feedRouter,
        address _initialOwner
    ) Ownable(_initialOwner) {
        KAIA = _kaia;
        USDT = _usdt;
        USDY = _usdy;
        kKAIA = _kkaia;
        kUSDT = _kusdt;
        feedRouter = IFeedRouter(_feedRouter);
    }

    // Bonding curve functions
    function getLpPrice(address lpToken) public view returns (uint256) {
        uint256 supply = IERC20(lpToken).totalSupply();
        return SLOPE_A * supply / PRECISION + BASE_PRICE_B;
    }

    function _calculateLpTokensToMint(address lpToken, uint256 depositAmount) private view returns (uint256) {
        uint256 currentPrice = getLpPrice(lpToken);

        // For linear bonding curve: tokens = amount / price
        return depositAmount * PRECISION / currentPrice;
    }

    function _calculateUnderlyingToReturn(address lpToken, uint256 lpAmount) private view returns (uint256) {
        uint256 currentPrice = getLpPrice(lpToken);
        return lpAmount * currentPrice / PRECISION;
    }

    // Lending functions
    function deposit(address token, uint256 amount) external nonReentrant {
        require(token == KAIA || token == USDT, "Invalid token");
        require(amount > 0, "Amount must be > 0");

        address lpToken = token == KAIA ? kKAIA : kUSDT;
        uint256 lpTokensToMint = _calculateLpTokensToMint(lpToken, amount);

        // Interactions
        IERC20(token).transferFrom(msg.sender, address(this), amount);
        ILPToken(lpToken).mint(msg.sender, lpTokensToMint);

        emit Deposit(msg.sender, token, amount, lpTokensToMint);
    }

    function redeem(address lpToken, uint256 amount) external nonReentrant {
        require(lpToken == kKAIA || lpToken == kUSDT, "Invalid LP token");
        require(amount > 0, "Amount must be > 0");
        require(IERC20(lpToken).balanceOf(msg.sender) >= amount, "Insufficient LP tokens");

        address underlyingToken = lpToken == kKAIA ? KAIA : USDT;
        uint256 underlyingAmount = _calculateUnderlyingToReturn(lpToken, amount);
        require(IERC20(underlyingToken).balanceOf(address(this)) >= underlyingAmount, "Insufficient liquidity");

        // Interactions
        ILPToken(lpToken).burn(msg.sender, amount);
        IERC20(underlyingToken).transfer(msg.sender, underlyingAmount);

        emit Redeem(msg.sender, lpToken, amount, underlyingAmount);
    }

    // Collateral functions
    function depositCollateral(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be > 0");

        // Effects
        collateralBalance[msg.sender] = collateralBalance[msg.sender] + amount;

        // Interactions
        IERC20(USDY).transferFrom(msg.sender, address(this), amount);

        emit CollateralDeposited(msg.sender, amount);
    }

    function withdrawCollateral(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be > 0");
        require(collateralBalance[msg.sender] >= amount, "Insufficient collateral");

        // Check if withdrawal would make position unsafe
        uint256 newCollateralBalance = collateralBalance[msg.sender] - amount;
        require(_isPositionSafe(msg.sender, newCollateralBalance), "Withdrawal would make position unsafe");

        // Effects
        collateralBalance[msg.sender] = newCollateralBalance;

        // Interactions
        IERC20(USDY).transfer(msg.sender, amount);

        emit CollateralWithdrawn(msg.sender, amount);
    }

    // Borrowing functions
    function borrow(address token, uint256 amount) external nonReentrant {
        require(token == KAIA || token == USDT, "Invalid token");
        require(amount > 0, "Amount must be > 0");
        require(IERC20(token).balanceOf(address(this)) >= amount, "Insufficient liquidity");

        // Update interest before borrowing
        _updateInterest(msg.sender);

        // Check if borrow would make position unsafe
        uint256 newDebtBalance = debtBalance[msg.sender][token] + amount;
        require(_isPositionSafeWithNewDebt(msg.sender, token, newDebtBalance), "Borrow would exceed LTV limit");

        // Effects
        debtBalance[msg.sender][token] = newDebtBalance;
        lastInterestUpdate[msg.sender] = block.timestamp;

        // Interactions
        IERC20(token).transfer(msg.sender, amount);

        emit Borrow(msg.sender, token, amount);
    }

    function repay(address token, uint256 amount) external nonReentrant {
        require(token == KAIA || token == USDT, "Invalid token");
        require(amount > 0, "Amount must be > 0");

        // Update interest first
        _updateInterest(msg.sender);

        uint256 currentDebt = debtBalance[msg.sender][token];
        require(currentDebt > 0, "No debt to repay");

        uint256 repayAmount = amount > currentDebt ? currentDebt : amount;

        // Effects
        debtBalance[msg.sender][token] = currentDebt - repayAmount;

        // Interactions
        IERC20(token).transferFrom(msg.sender, address(this), repayAmount);

        emit Repay(msg.sender, token, repayAmount);
    }

    // Interest calculation
    function _updateInterest(address borrower) internal {
        uint256 lastUpdate = lastInterestUpdate[borrower];
        if (lastUpdate == 0) {
            lastInterestUpdate[borrower] = block.timestamp;
            return;
        }

        uint256 timeElapsed = block.timestamp - lastUpdate;
        if (timeElapsed == 0) return;

        // Update KAIA debt
        uint256 kaiaDebt = debtBalance[borrower][KAIA];
        if (kaiaDebt > 0) {
            uint256 interest = kaiaDebt * ANNUAL_INTEREST_RATE * timeElapsed / 100 / SECONDS_PER_YEAR;
            debtBalance[borrower][KAIA] = kaiaDebt + interest;
        }

        // Update USDT debt
        uint256 usdtDebt = debtBalance[borrower][USDT];
        if (usdtDebt > 0) {
            uint256 interest = usdtDebt * ANNUAL_INTEREST_RATE * timeElapsed / 100 / SECONDS_PER_YEAR;
            debtBalance[borrower][USDT] = usdtDebt + interest;
        }

        lastInterestUpdate[borrower] = block.timestamp;
    }

    // Liquidation
    function liquidate(address borrower, uint256 repayAmount) external nonReentrant {
        require(isLiquidatable(borrower), "Position is not liquidatable");
        require(repayAmount > 0, "Repay amount must be > 0");

        // Update interest before liquidation
        _updateInterest(borrower);

        // Determine which token to repay (prioritize KAIA for simplicity)
        address repayToken = debtBalance[borrower][KAIA] > 0 ? KAIA : USDT;
        uint256 currentDebt = debtBalance[borrower][repayToken];
        require(currentDebt > 0, "No debt to repay");

        uint256 actualRepayAmount = repayAmount > currentDebt ? currentDebt : repayAmount;

        // Calculate collateral to seize (with 10% penalty)
        uint256 repayValueUSD = _getTokenValueUSD(repayToken, actualRepayAmount);
        uint256 collateralToSeize = repayValueUSD * (100 + LIQUIDATION_PENALTY) / 100 / USDT_COLLATERAL_PRICE;

        require(collateralBalance[borrower] >= collateralToSeize, "Insufficient collateral");

        // Effects
        debtBalance[borrower][repayToken] = currentDebt - actualRepayAmount;
        collateralBalance[borrower] = collateralBalance[borrower] - collateralToSeize;

        // Interactions
        IERC20(repayToken).transferFrom(msg.sender, address(this), actualRepayAmount);
        IERC20(USDY).transfer(msg.sender, collateralToSeize);

        emit Liquidate(msg.sender, borrower, actualRepayAmount, collateralToSeize);
    }

    // View functions
    function getLTV(address borrower) public view returns (uint256) {
        uint256 collateralValueUSD = collateralBalance[borrower] * USDT_COLLATERAL_PRICE / PRECISION;
        if (collateralValueUSD == 0) return 0;

        uint256 totalDebtUSD = _getTotalDebtValueUSD(borrower);
        return totalDebtUSD * 100 * PRECISION / collateralValueUSD;
    }

    function isLiquidatable(address borrower) public view returns (bool) {
        return getLTV(borrower) > MAX_LTV * PRECISION;
    }

    // Internal helper functions
    function _isPositionSafe(address borrower, uint256 newCollateralBalance) internal view returns (bool) {
        uint256 collateralValueUSD = newCollateralBalance * USDT_COLLATERAL_PRICE / PRECISION;
        uint256 totalDebtUSD = _getTotalDebtValueUSD(borrower);

        if (collateralValueUSD == 0 && totalDebtUSD > 0) return false;
        if (totalDebtUSD == 0) return true;

        return totalDebtUSD * 100 * PRECISION / collateralValueUSD <= MAX_LTV * PRECISION;
    }

    function _isPositionSafeWithNewDebt(address borrower, address token, uint256 newDebtBalance) internal view returns (bool) {
        uint256 collateralValueUSD = collateralBalance[borrower] * USDT_COLLATERAL_PRICE / PRECISION;

        uint256 otherTokenDebt = token == KAIA ? debtBalance[borrower][USDT] : debtBalance[borrower][KAIA];
        address otherToken = token == KAIA ? USDT : KAIA;

        uint256 newTokenDebtUSD = _getTokenValueUSD(token, newDebtBalance);
        uint256 otherTokenDebtUSD = _getTokenValueUSD(otherToken, otherTokenDebt);
        uint256 totalDebtUSD = newTokenDebtUSD + otherTokenDebtUSD;

        if (collateralValueUSD == 0 && totalDebtUSD > 0) return false;
        if (totalDebtUSD == 0) return true;

        return totalDebtUSD * 100 * PRECISION / collateralValueUSD <= MAX_LTV * PRECISION;
    }

    function _getTotalDebtValueUSD(address borrower) internal view returns (uint256) {
        uint256 kaiaDebtUSD = _getTokenValueUSD(KAIA, debtBalance[borrower][KAIA]);
        uint256 usdtDebtUSD = _getTokenValueUSD(USDT, debtBalance[borrower][USDT]);
        return kaiaDebtUSD + usdtDebtUSD;
    }

    /**
     * @notice Get KAIA price from Orakl Network price feed with fallback
     * @return price KAIA price in USD (18 decimals)
     */
    function getKaiaPrice() public pure returns (uint256) {
        // For testing: use fallback price directly
        // In production, uncomment the oracle code below
        return _getFallbackKaiaPrice();

        /*
        try feedRouter.latestRoundData(KAIA_FEED_NAME) returns (
            uint64 id,
            int256 answer,
            uint256 updatedAt
        ) {
            // Check for stale data
            if (block.timestamp - updatedAt > PRICE_STALENESS_THRESHOLD) {
                return _getFallbackKaiaPrice();
            }

            // Check for invalid price
            if (answer <= 0) {
                return _getFallbackKaiaPrice();
            }

            // Get feed decimals and normalize to 18 decimals
            uint8 feedDecimals = feedRouter.decimals(KAIA_FEED_NAME);
            uint256 price = uint256(answer);

            if (feedDecimals < 18) {
                price = price * (10 ** (18 - feedDecimals));
            } else if (feedDecimals > 18) {
                price = price / (10 ** (feedDecimals - 18));
            }

            return price;
        } catch {
            // Fallback to hardcoded price for testing
            return _getFallbackKaiaPrice();
        }
        */
    }

    /**
     * @notice Fallback KAIA price for testing (owner can update)
     * @return Fallback KAIA price ($0.15 as of current market)
     */
    function _getFallbackKaiaPrice() internal pure returns (uint256) {
        return 15e16; // $0.15 in 18 decimals
    }

    /**
     * @notice Get USDT price (using DummyUSDT contract for decimals, assuming $1 peg)
     * @return price USDT price in USD (18 decimals) - always $1
     */
    function getUsdtPrice() public pure returns (uint256) {
        return PRECISION; // $1.00 in 18 decimals
    }

    function _getTokenValueUSD(address token, uint256 amount) internal view returns (uint256) {
        if (token == KAIA) {
            uint256 kaiaPrice = getKaiaPrice();
            return amount * kaiaPrice / PRECISION;
        } else if (token == USDT) {
            uint256 usdtPrice = getUsdtPrice();
            return amount * usdtPrice / PRECISION;
        }
        return 0;
    }

    // Additional view functions using OpenZeppelin
    function getTokenDecimals(address token) external view returns (uint8) {
        return IERC20Metadata(token).decimals();
    }

    function getTokenSymbol(address token) external view returns (string memory) {
        return IERC20Metadata(token).symbol();
    }

    function getTokenName(address token) external view returns (string memory) {
        return IERC20Metadata(token).name();
    }

    // Emergency functions (Owner only)
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        IERC20(token).transfer(owner(), amount);
    }

    function updateTokenPrice(address token, uint256 newPrice) external onlyOwner {
        // This would require making prices non-constant in a real implementation
        // For now, keeping hardcoded prices as per original design
    }
}

contracts/LPToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title LPToken
 * @dev LP Token for the lending protocol with mint/burn functionality
 * @notice This token represents liquidity provider shares in the lending pool
 */
contract LPToken is ERC20, Ownable {

    address public lendingProtocol;

    event Minted(address indexed to, uint256 amount);
    event Burned(address indexed from, uint256 amount);
    event LendingProtocolUpdated(address indexed oldProtocol, address indexed newProtocol);

    modifier onlyLendingProtocol() {
        require(msg.sender == lendingProtocol, "Only lending protocol can call");
        _;
    }

    constructor(
        string memory name,
        string memory symbol,
        address _lendingProtocol,
        address _owner
    ) ERC20(name, symbol) Ownable(_owner) {
        lendingProtocol = _lendingProtocol;
    }

    /**
     * @notice Mint LP tokens to address (only lending protocol)
     * @param to Address to mint tokens to
     * @param amount Amount to mint
     */
    function mint(address to, uint256 amount) external onlyLendingProtocol {
        _mint(to, amount);
        emit Minted(to, amount);
    }

    /**
     * @notice Burn LP tokens from address (only lending protocol)
     * @param from Address to burn tokens from
     * @param amount Amount to burn
     */
    function burn(address from, uint256 amount) external onlyLendingProtocol {
        _burn(from, amount);
        emit Burned(from, amount);
    }

    /**
     * @notice Update the lending protocol address (only owner)
     * @param _newLendingProtocol New lending protocol address
     */
    function updateLendingProtocol(address _newLendingProtocol) external onlyOwner {
        require(_newLendingProtocol != address(0), "Invalid address");
        address oldProtocol = lendingProtocol;
        lendingProtocol = _newLendingProtocol;
        emit LendingProtocolUpdated(oldProtocol, _newLendingProtocol);
    }
}

contracts/SplitBilling.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title SplitBilling  
 * @dev Contract for creating split bills where each person pays their own share directly to payee
 * @notice Allows creating split payment requests with direct payments - no funds held by contract
 * @notice Each payment is immediately transferred to the payee with on-chain verification
 */
contract SplitBilling is ReentrancyGuard {

    // Constants
    uint256 public constant MAX_RECIPIENTS = 20;
    uint256 public constant GAS_LIMIT = 50000;
    address public constant ETH_TOKEN = address(0);

    // Structs
    struct SplitRequest {
        address creator;
        address payee; // Who receives the money (can be creator or someone else)
        address token;
        uint256 totalAmount;
        uint256 totalPaid;
        uint256 deadline;
        bool cancelled;
        string description;
        address[] debtors;
        uint256[] amounts;
        mapping(address => uint256) owedAmounts;
        mapping(address => uint256) paidAmounts;
        mapping(address => bool) hasPaid;
    }

    // Storage
    mapping(uint256 => SplitRequest) public splitRequests;
    mapping(address => uint256[]) public userSplits; // Track splits per user
    uint256 public splitCounter;

    // Events
    event SplitCreated(
        uint256 indexed splitId,
        address indexed creator,
        address indexed payee,
        address token,
        uint256 totalAmount,
        uint256 deadline,
        string description,
        address[] debtors,
        uint256[] amounts
    );

    event PaymentMade(
        uint256 indexed splitId,
        address indexed payer,
        address token,
        uint256 amount,
        uint256 totalPaid
    );

    event SplitCompleted(
        uint256 indexed splitId,
        address indexed payee,
        address token,
        uint256 totalAmount
    );

    event SplitCancelled(
        uint256 indexed splitId,
        address indexed creator
    );

    event FundsWithdrawn(
        uint256 indexed splitId,
        address indexed payee,
        address token,
        uint256 amount
    );

    event DeadlineAlert(
        uint256 indexed splitId,
        address indexed debtor,
        uint256 amountOwed,
        uint256 deadline
    );

    // Custom errors
    error InvalidInput();
    error SplitNotFound();
    error SplitExpired();
    error SplitAlreadyCancelled();
    error AlreadyPaid();
    error InvalidAmount();
    error NotAuthorized();
    error TransferFailed();
    error SplitNotCompleted();

    /**
     * @dev Create a split payment request
     * @param payee Address who will receive the collected funds
     * @param debtors Array of addresses who owe money
     * @param amounts Array of amounts each debtor owes
     * @param token Token address (address(0) for ETH)
     * @param deadline Unix timestamp deadline for payments
     * @param description Description of the split bill
     */
    function createSplit(
        address payee,
        address[] calldata debtors,
        uint256[] calldata amounts,
        address token,
        uint256 deadline,
        string calldata description
    ) external returns (uint256 splitId) {
        // Input validation
        if (debtors.length == 0 || debtors.length > MAX_RECIPIENTS) revert InvalidInput();
        if (debtors.length != amounts.length) revert InvalidInput();
        if (deadline <= block.timestamp) revert InvalidInput();
        if (payee == address(0)) revert InvalidInput();

        // Calculate total amount and validate
        uint256 totalAmount = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            if (debtors[i] == address(0) || amounts[i] == 0) revert InvalidInput();
            totalAmount += amounts[i];
        }

        if (totalAmount == 0) revert InvalidAmount();

        // Create split request
        splitId = ++splitCounter;
        SplitRequest storage split = splitRequests[splitId];
        split.creator = msg.sender;
        split.payee = payee;
        split.token = token;
        split.totalAmount = totalAmount;
        split.deadline = deadline;
        split.description = description;
        split.debtors = debtors;
        split.amounts = amounts;

        // Set owed amounts for each debtor
        for (uint256 i = 0; i < debtors.length; i++) {
            split.owedAmounts[debtors[i]] = amounts[i];
            userSplits[debtors[i]].push(splitId);
        }

        // Also add to creator's tracking
        userSplits[msg.sender].push(splitId);

        emit SplitCreated(splitId, msg.sender, payee, token, totalAmount, deadline, description, debtors, amounts);
    }

    /**
     * @dev Pay your share of a split bill
     * @param splitId The ID of the split to pay
     */
    function payShare(uint256 splitId) external payable nonReentrant {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();

        SplitRequest storage split = splitRequests[splitId];
        if (split.cancelled) revert SplitAlreadyCancelled();
        if (block.timestamp > split.deadline) revert SplitExpired();
        if (split.hasPaid[msg.sender]) revert AlreadyPaid();

        uint256 owedAmount = split.owedAmounts[msg.sender];
        if (owedAmount == 0) revert InvalidInput(); // Not part of this split

        // Handle payment - DIRECT TRANSFER TO PAYEE
        if (split.token == ETH_TOKEN) {
            if (msg.value != owedAmount) revert InvalidAmount();
            // Send ETH directly to payee
            (bool success, ) = split.payee.call{value: owedAmount, gas: GAS_LIMIT}("");
            if (!success) revert TransferFailed();
        } else {
            // Transfer tokens directly to payee
            IERC20(split.token).transferFrom(msg.sender, split.payee, owedAmount);
        }

        // Update payment tracking
        split.paidAmounts[msg.sender] = owedAmount;
        split.hasPaid[msg.sender] = true;
        split.totalPaid += owedAmount;

        emit PaymentMade(splitId, msg.sender, split.token, owedAmount, split.totalPaid);

        // Check if split is now complete (no need to transfer, already done above)
        if (split.totalPaid == split.totalAmount) {
            emit SplitCompleted(splitId, split.payee, split.token, split.totalAmount);
        }
    }

    /**
     * @dev Pay someone else's share (useful for covering friends)
     * @param splitId The ID of the split
     * @param debtor Address of the person whose share you're paying
     */
    function payForSomeone(uint256 splitId, address debtor) external payable nonReentrant {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();

        SplitRequest storage split = splitRequests[splitId];
        if (split.cancelled) revert SplitAlreadyCancelled();
        if (block.timestamp > split.deadline) revert SplitExpired();
        if (split.hasPaid[debtor]) revert AlreadyPaid();

        uint256 owedAmount = split.owedAmounts[debtor];
        if (owedAmount == 0) revert InvalidInput();

        // Handle payment - DIRECT TRANSFER TO PAYEE
        if (split.token == ETH_TOKEN) {
            if (msg.value != owedAmount) revert InvalidAmount();
            // Send ETH directly to payee
            (bool success, ) = split.payee.call{value: owedAmount, gas: GAS_LIMIT}("");
            if (!success) revert TransferFailed();
        } else {
            // Transfer tokens directly to payee
            IERC20(split.token).transferFrom(msg.sender, split.payee, owedAmount);
        }

        // Update payment tracking
        split.paidAmounts[debtor] = owedAmount;
        split.hasPaid[debtor] = true;
        split.totalPaid += owedAmount;

        emit PaymentMade(splitId, debtor, split.token, owedAmount, split.totalPaid);

        // Check if split is now complete (no need to transfer, already done above)
        if (split.totalPaid == split.totalAmount) {
            emit SplitCompleted(splitId, split.payee, split.token, split.totalAmount);
        }
    }

    // Note: _completeSplit function removed as payments are now direct
    // Each payment goes directly to the payee, no funds held by contract

    /**
     * @dev Cancel a split (only creator, only if no payments made)
     * @param splitId The ID of the split to cancel
     */
    function cancelSplit(uint256 splitId) external {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();

        SplitRequest storage split = splitRequests[splitId];
        if (msg.sender != split.creator) revert NotAuthorized();
        if (split.totalPaid > 0) revert InvalidInput(); // Can't cancel if payments made

        split.cancelled = true;
        emit SplitCancelled(splitId, msg.sender);
    }

    /**
     * @dev Check for unpaid debtors after deadline
     * @param splitId The ID of the split to check
     */
    function checkOverduePayments(uint256 splitId) external {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();

        SplitRequest storage split = splitRequests[splitId];
        if (block.timestamp <= split.deadline) revert InvalidInput();

        for (uint256 i = 0; i < split.debtors.length; i++) {
            address debtor = split.debtors[i];
            if (!split.hasPaid[debtor]) {
                emit DeadlineAlert(splitId, debtor, split.owedAmounts[debtor], split.deadline);
            }
        }
    }

    // View Functions

    /**
     * @dev Get split details
     */
    function getSplitDetails(uint256 splitId) external view returns (
        address creator,
        address payee,
        address token,
        uint256 totalAmount,
        uint256 totalPaid,
        uint256 deadline,
        bool cancelled,
        string memory description,
        address[] memory debtors,
        uint256[] memory amounts
    ) {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();

        SplitRequest storage split = splitRequests[splitId];
        return (
            split.creator,
            split.payee,
            split.token,
            split.totalAmount,
            split.totalPaid,
            split.deadline,
            split.cancelled,
            split.description,
            split.debtors,
            split.amounts
        );
    }

    /**
     * @dev Check how much someone owes in a split
     */
    function getOwedAmount(uint256 splitId, address debtor) external view returns (uint256) {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();
        return splitRequests[splitId].owedAmounts[debtor];
    }

    /**
     * @dev Check if someone has paid their share
     */
    function hasPaid(uint256 splitId, address debtor) external view returns (bool) {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();
        return splitRequests[splitId].hasPaid[debtor];
    }

    /**
     * @dev Get how much someone has paid
     */
    function getPaidAmount(uint256 splitId, address debtor) external view returns (uint256) {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();
        return splitRequests[splitId].paidAmounts[debtor];
    }

    /**
     * @dev Get all splits for a user
     */
    function getUserSplits(address user) external view returns (uint256[] memory) {
        return userSplits[user];
    }

    /**
     * @dev Check if split is fully paid
     */
    function isSplitComplete(uint256 splitId) external view returns (bool) {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();
        SplitRequest storage split = splitRequests[splitId];
        return split.totalPaid == split.totalAmount;
    }

    // Accept ETH
    receive() external payable {}
}

contracts/USDY.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title USDY - USD Yield Token (Real World Asset Demo)
 * @notice A demonstration RWA token representing real-world assets
 * @dev ERC20 token with faucet functionality for testing RWA lending scenarios
 * 
 * This contract demonstrates how real-world asset tokens could work:
 * - Users can claim tokens from faucet (simulating RWA token acquisition)
 * - Tokens can be used as collateral in lending protocols
 * - Represents $1 USD value per token (pegged to USD)
 * - Includes anti-spam measures with time-based claiming limits
 */
contract USDY is ERC20, Ownable, ReentrancyGuard {

    // Faucet configuration
    uint256 public constant FAUCET_AMOUNT = 1000e18; // 1000 USDY per claim
    uint256 public constant FAUCET_COOLDOWN = 24 hours; // 24 hour cooldown
    uint256 public constant MAX_SUPPLY = 1000000e18; // 1M USDY max supply

    // Faucet state
    mapping(address => uint256) public lastFaucetClaim;
    uint256 public totalFaucetClaimed;

    // RWA metadata
    string public constant RWA_TYPE = "USD_YIELD_BEARING";
    string public constant BACKING_ASSET = "US_TREASURY_BONDS";
    uint256 public constant ASSET_VALUE_USD = 1e18; // $1 per token

    // Events
    event FaucetClaimed(address indexed user, uint256 amount, uint256 timestamp);
    event FaucetConfigured(uint256 newAmount, uint256 newCooldown);
    event RWATokenMinted(address indexed to, uint256 amount, string reason);

    // Errors
    error FaucetCooldownNotMet(uint256 timeRemaining);
    error FaucetSupplyExhausted();
    error InvalidAmount();
    error MaxSupplyExceeded();

    constructor() ERC20("USD Yield Token", "USDY") Ownable(msg.sender) {
        // Mint initial supply to owner for liquidity
        _mint(msg.sender, 100000e18); // 100k USDY for initial liquidity
    }

    /**
     * @notice Claim tokens from faucet (simulates acquiring RWA tokens)
     * @dev Users can claim once every 24 hours to prevent spam
     */
    function claimFromFaucet() external nonReentrant {
        address user = msg.sender;

        // Check cooldown period
        if (block.timestamp < lastFaucetClaim[user] + FAUCET_COOLDOWN) {
            uint256 timeRemaining = (lastFaucetClaim[user] + FAUCET_COOLDOWN) - block.timestamp;
            revert FaucetCooldownNotMet(timeRemaining);
        }

        // Check if faucet has supply remaining
        if (totalFaucetClaimed + FAUCET_AMOUNT > MAX_SUPPLY - totalSupply() + totalFaucetClaimed) {
            revert FaucetSupplyExhausted();
        }

        // Update state
        lastFaucetClaim[user] = block.timestamp;
        totalFaucetClaimed += FAUCET_AMOUNT;

        // Mint tokens to user
        _mint(user, FAUCET_AMOUNT);

        emit FaucetClaimed(user, FAUCET_AMOUNT, block.timestamp);
    }

    /**
     * @notice Check how much time until user can claim from faucet again
     * @param user Address to check
     * @return timeRemaining Seconds until next claim is available (0 if ready)
     */
    function timeUntilNextClaim(address user) external view returns (uint256) {
        uint256 nextClaimTime = lastFaucetClaim[user] + FAUCET_COOLDOWN;
        if (block.timestamp >= nextClaimTime) {
            return 0;
        }
        return nextClaimTime - block.timestamp;
    }

    /**
     * @notice Check if user can claim from faucet
     * @param user Address to check
     * @return canClaim True if user can claim now
     */
    function canClaimFromFaucet(address user) external view returns (bool) {
        return block.timestamp >= lastFaucetClaim[user] + FAUCET_COOLDOWN &&
               totalFaucetClaimed + FAUCET_AMOUNT <= MAX_SUPPLY - totalSupply() + totalFaucetClaimed;
    }

    /**
     * @notice Get faucet information
     * @return amount Amount per claim
     * @return cooldown Cooldown period in seconds
     * @return totalClaimed Total amount claimed from faucet
     * @return remainingSupply Remaining faucet supply
     */
    function getFaucetInfo() external view returns (
        uint256 amount,
        uint256 cooldown,
        uint256 totalClaimed,
        uint256 remainingSupply
    ) {
        amount = FAUCET_AMOUNT;
        cooldown = FAUCET_COOLDOWN;
        totalClaimed = totalFaucetClaimed;

        uint256 maxFaucetSupply = MAX_SUPPLY - (totalSupply() - totalFaucetClaimed);
        remainingSupply = maxFaucetSupply > totalFaucetClaimed ? 
            maxFaucetSupply - totalFaucetClaimed : 0;
    }

    /**
     * @notice Emergency mint function for owner (for special circumstances)
     * @param to Address to mint to
     * @param amount Amount to mint
     * @param reason Reason for minting
     */
    function emergencyMint(address to, uint256 amount, string calldata reason) 
        external 
        onlyOwner 
        nonReentrant 
    {
        if (amount == 0) revert InvalidAmount();
        if (totalSupply() + amount > MAX_SUPPLY) revert MaxSupplyExceeded();

        _mint(to, amount);
        emit RWATokenMinted(to, amount, reason);
    }

    /**
     * @notice Burn tokens (for RWA redemption scenarios)
     * @param amount Amount to burn
     */
    function burn(uint256 amount) external {
        if (amount == 0) revert InvalidAmount();
        _burn(msg.sender, amount);
    }

    /**
     * @notice Burn tokens from specific address (with allowance)
     * @param from Address to burn from
     * @param amount Amount to burn
     */
    function burnFrom(address from, uint256 amount) external {
        if (amount == 0) revert InvalidAmount();

        uint256 currentAllowance = allowance(from, msg.sender);
        require(currentAllowance >= amount, "ERC20: burn amount exceeds allowance");

        _approve(from, msg.sender, currentAllowance - amount);
        _burn(from, amount);
    }

    /**
     * @notice Get RWA token metadata
     * @return rwaType Type of RWA
     * @return backingAsset What backs this token
     * @return valueUSD USD value per token
     * @return maxSupply Maximum token supply
     */
    function getRWAMetadata() external pure returns (
        string memory rwaType,
        string memory backingAsset,
        uint256 valueUSD,
        uint256 maxSupply
    ) {
        return (RWA_TYPE, BACKING_ASSET, ASSET_VALUE_USD, MAX_SUPPLY);
    }

    /**
     * @notice Override decimals to match USD (18 decimals)
     */
    function decimals() public pure override returns (uint8) {
        return 18;
    }
}

