                curveExponent: 3 * 1e18,    // Cubic increase (steeper)
                maxRate: 2000,              // 20% maximum rate

                minPenalty: 1000,           // 10% minimum penalty (higher risk)
                maxPenalty: 2000,           // 20% maximum penalty
                penaltyCurveSlope: 150,     // Faster penalty increases

                baseHaircut: 800,           // 8% base haircut (more volatile)
                volatilityFactor: 1500,     // Higher volatility sensitivity
                maxHaircut: 2500,           // 25% maximum haircut

                ltvDecayRate: 75,           // 0.75% LTV decay (faster)
                maxDecayPeriod: 120 days    // 4-month decay
            });

        lending.addAssetWithBondingCurves(
            deployedContracts.tokenizedSilver,
            6500,   // 65% base LTV (lower than gold)
            7500,   // 75% liquidation threshold
            "tSILVER-USD",
            false,  // canBorrow (collateral only)
            true,   // canCollateralize
            silverCurve
        );

        emit BondingCurveConfigured(deployedContracts.tokenizedSilver, "Silver");
    }

    function _configureRealEstateWithBondingCurves(RWALendingPlatformWithBondingCurves lending) internal {
        RWALendingPlatformWithBondingCurves.BondingCurveConfig memory realEstateCurve = 
            RWALendingPlatformWithBondingCurves.BondingCurveConfig({
                baseRate: 250,              // 2.5% base (most stable)
                optimalUtilization: 8000,   // 80% optimal (very stable)
                curveExponent: 2 * 1e18,    // Quadratic increase
                maxRate: 1200,              // 12% maximum

                minPenalty: 600,            // 6% minimum (lowest risk)
                maxPenalty: 1200,           // 12% maximum
                penaltyCurveSlope: 75,      // Gentle penalty increases

                baseHaircut: 300,           // 3% base (most stable)
                volatilityFactor: 500,      // Low volatility sensitivity
                maxHaircut: 1500,           // 15% maximum

                ltvDecayRate: 25,           // 0.25% decay (slowest)
                maxDecayPeriod: 365 days    // 1-year decay
            });

        lending.addAssetWithBondingCurves(
            deployedContracts.tokenizedRealEstate,
            8000,   // 80% base LTV (highest, most stable)
            9000,   // 90% liquidation threshold
            "tREAL-USD",
            false,  // canBorrow (collateral only)
            true,   // canCollateralize
            realEstateCurve
        );

        emit BondingCurveConfigured(deployedContracts.tokenizedRealEstate, "RealEstate");
    }

    function _configureDummyUSDTWithBondingCurves(RWALendingPlatformWithBondingCurves lending) internal {
        // Configure our existing DummyUSDT as the borrowable asset
        RWALendingPlatformWithBondingCurves.BondingCurveConfig memory usdtCurve = 
            RWALendingPlatformWithBondingCurves.BondingCurveConfig({
                baseRate: 200,              // 2% base borrowing rate
                optimalUtilization: 8000,   // 80% optimal utilization
                curveExponent: 2 * 1e18,    // Quadratic supply curve
                maxRate: 1500,              // 15% maximum rate

                minPenalty: 0,              // No liquidation penalty (stable asset)
                maxPenalty: 0,
                penaltyCurveSlope: 0,

                baseHaircut: 0,             // No haircut (borrowable, not collateral)
                volatilityFactor: 0,
                maxHaircut: 0,

                ltvDecayRate: 0,            // No LTV decay
                maxDecayPeriod: 0
            });

        lending.addAssetWithBondingCurves(
            KAIA_DUMMY_USDT,
            0,      // No LTV (not collateral)
            0,      // No liquidation threshold
            "USDT-USD", // Price feed (should be 1.0)
            true,   // canBorrow (this is what users borrow)
            false,  // canCollateralize (not used as collateral)
            usdtCurve
        );

        emit BondingCurveConfigured(KAIA_DUMMY_USDT, "DummyUSDT");
    }

    function getContracts() external view returns (DeployedContracts memory) {
        return deployedContracts;
    }
}

/**
 * @title Kaia-Compatible Bonding Curve Demo
 * @notice Demo contract using existing DummyUSDT and new RWA tokens
 */
contract KaiaBondingCurveDemo {
    RWALendingPlatformWithBondingCurves public platform;
    address public goldToken;
    address public silverToken;
    address public realEstateToken;
    address public dummyUSDT;

    event DemoStepCompleted(string step, address user, uint256 value);
    event BondingCurveShowcase(string curveType, uint256 inputValue, uint256 outputValue);

    constructor(
        address _platform,
        address _gold,
        address _silver, 
        address _realEstate,
        address _dummyUSDT
    ) {
        platform = RWALendingPlatformWithBondingCurves(_platform);
        goldToken = _gold;
        silverToken = _silver;
        realEstateToken = _realEstate;
        dummyUSDT = _dummyUSDT;
    }

    /**
     * @notice Complete demo using existing DummyUSDT ecosystem
     */
    function runKaiaRWADemo(address user) external {

        // Step 1: Get RWA tokens from faucets (matching DummyUSDT pattern)
        TokenizedGold(goldToken).faucet();        // 20 grams gold (~$1,300)
        TokenizedSilver(silverToken).faucet();    // 40 ounces silver (~$1,200)  
        TokenizedRealEstate(realEstateToken).faucet(); // $10,000 real estate

        // Step 2: User gets DummyUSDT from existing faucet
        // (User calls DummyUSDT(0x07bA937403023CcD444923B183d42438b7057811).faucet())

        // Step 3: Deposit RWA as collateral with bonding curve calculations
        TokenizedGold(goldToken).approve(address(platform), 20 * 1e18);
        TokenizedSilver(silverToken).approve(address(platform), 40 * 1e18);
        TokenizedRealEstate(realEstateToken).approve(address(platform), 10 * 1e18);

        platform.deposit(goldToken, 20 * 1e18);
        platform.deposit(silverToken, 40 * 1e18);  
        platform.deposit(realEstateToken, 10 * 1e18);

        emit DemoStepCompleted("DepositRWACollateral", user, 12500); // ~$12.5k total

        // Step 4: Borrow DummyUSDT using bonding curve LTV
        uint256 borrowAmount = 8000 * 1e18; // $8,000 DUSDT (conservative LTV)
        platform.borrow(dummyUSDT, borrowAmount);

        emit DemoStepCompleted("BorrowDummyUSDT", user, borrowAmount);

        // Step 5: Show bonding curve analytics
        (uint256 goldRate,,,, uint256 goldCurve) = platform.getBondingCurveMetrics(goldToken);
        emit BondingCurveShowcase("GoldInterestRate", 0, goldRate);

        // Step 6: Repay using existing DummyUSDT
        IERC20(dummyUSDT).approve(address(platform), borrowAmount);
        platform.repay(dummyUSDT, borrowAmount);

        emit DemoStepCompleted("RepayWithDummyUSDT", user, borrowAmount);
    }

    function getTokenBalances(address user) external view returns (
        uint256 goldBalance,
        uint256 silverBalance,
        uint256 realEstateBalance,
        uint256 dummyUSDTBalance
    ) {
        goldBalance = IERC20(goldToken).balanceOf(user);
        silverBalance = IERC20(silverToken).balanceOf(user);
        realEstateBalance = IERC20(realEstateToken).balanceOf(user);
        dummyUSDTBalance = IERC20(dummyUSDT).balanceOf(user);
    }
}

contracts/RWALendingPlatformWithBondingC.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@bisonai/orakl-contracts/v0.2/src/interfaces/IFeedRouter.sol";
import "@bisonai/orakl-contracts/v0.2/src/interfaces/IFeed.sol";

/**
 * @title RWA-Backed DeFi Lending Platform with Bonding Curves
 * @notice Advanced lending protocol using bonding curves for dynamic pricing and risk management
 * @dev Integrates Orakl Network for real-time pricing with mathematical bonding curves
 * 
 * BONDING CURVE IMPLEMENTATIONS:
 * 1. Interest Rate Curves: Exponential curves based on utilization
 * 2. Liquidation Penalty Curves: Higher penalties for riskier positions  
 * 3. Collateral Valuation Curves: Dynamic haircuts based on volatility
 * 4. LTV Decay Curves: Time-based LTV adjustments
 */
contract RWALendingPlatformWithBondingCurves is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // =============================================================================
    // CONSTANTS & IMMUTABLES
    // =============================================================================

    /// @notice Mathematical precision for bonding curve calculations
    uint256 public constant PRECISION = 1e18;
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant SECONDS_PER_YEAR = 365 days;

    /// @notice Orakl Network Feed Router for price data
    IFeedRouter public immutable oraklRouter;

    // =============================================================================
    // STATE VARIABLES
    // =============================================================================

    address public feeRecipient;
    uint256 public platformFee = 500; // 5%
    uint256 public gracePeriod = 24 hours;

    // =============================================================================
    // BONDING CURVE PARAMETERS
    // =============================================================================

    /// @notice Bonding curve configuration for each asset
    struct BondingCurveConfig {
        // Interest Rate Curve Parameters (Exponential)
        uint256 baseRate;           // Base interest rate (basis points)
        uint256 optimalUtilization; // Optimal utilization point (basis points)
        uint256 curveExponent;      // Exponential curve steepness (scaled by 1e18)
        uint256 maxRate;            // Maximum interest rate cap

        // Liquidation Penalty Curve (Progressive)
        uint256 minPenalty;         // Minimum liquidation penalty
        uint256 maxPenalty;         // Maximum liquidation penalty  
        uint256 penaltyCurveSlope;  // How quickly penalty increases

        // Collateral Haircut Curve (Volatility-based)
        uint256 baseHaircut;        // Base collateral haircut (basis points)
        uint256 volatilityFactor;   // Volatility multiplier
        uint256 maxHaircut;         // Maximum haircut allowed

        // Time Decay Parameters
        uint256 ltvDecayRate;       // How fast LTV decays over time
        uint256 maxDecayPeriod;     // Maximum decay period
    }

    /// @notice Asset configuration with enhanced bonding curve features
    struct AssetConfig {
        bool isSupported;
        bool canBorrow;
        bool canCollateralize;
        uint256 baseLTV;                // Base LTV before bonding curve adjustments
        uint256 liquidationThreshold;
        string oraklFeedName;
        uint256 lastInterestUpdate;
        uint256 totalDeposits;
        uint256 totalBorrows;
        uint256 accruedInterest;
        uint256 reserveFactor;

        // Bonding curve configuration
        BondingCurveConfig bondingCurve;

        // Historical data for curve calculations
        uint256 avgVolatility;          // Rolling average volatility
        uint256 lastPriceUpdate;        // Last price update timestamp
        uint256 priceHistory;           // Simple price history for volatility
        uint256 fallbackPrice;          // Fallback price when oracle is unavailable
    }

    /// @notice Enhanced user position with time-based metrics
    struct UserPosition {
        mapping(address => uint256) deposits;
        mapping(address => uint256) borrows;
        mapping(address => uint256) lastBorrowUpdate;
        mapping(address => uint256) positionOpenTime; // When position was opened
        bool inGracePeriod;
        uint256 gracePeriodEnd;
        uint256 warningCount;
        uint256 totalLiquidations;      // Historical liquidation count
        uint256 riskScore;              // Calculated risk score for bonding curves
    }

    // =============================================================================
    // MAPPINGS & STORAGE
    // =============================================================================

    mapping(address => AssetConfig) public assets;
    mapping(address => UserPosition) private userPositions;
    address[] public supportedAssets;
    mapping(address => mapping(address => uint256)) public lenderInterest;

    // =============================================================================
    // EVENTS
    // =============================================================================

    event AssetAdded(address indexed asset, BondingCurveConfig curveConfig);
    event BondingCurveUpdated(address indexed asset, string curveType, uint256 newValue);
    event DynamicRateCalculated(address indexed asset, uint256 utilization, uint256 newRate, uint256 curveValue);
    event LiquidationPenaltyCalculated(address indexed borrower, uint256 healthFactor, uint256 penalty);
    event CollateralHaircutApplied(address indexed asset, uint256 volatility, uint256 haircut);
    event LTVDecayApplied(address indexed user, address indexed asset, uint256 newLTV, uint256 timePassed);

    // Standard lending events
    event Deposited(address indexed user, address indexed asset, uint256 amount, uint256 newBalance);
    event Borrowed(address indexed user, address indexed asset, uint256 amount, uint256 effectiveLTV);
    event Repaid(address indexed user, address indexed asset, uint256 amount, uint256 remainingDebt);
    event Withdrawn(address indexed user, address indexed asset, uint256 amount, uint256 newBalance);
    event InterestClaimed(address indexed user, address indexed asset, uint256 amount);
    event Liquidated(address indexed borrower, address indexed liquidator, address indexed collateralAsset, 
                    uint256 collateralSeized, uint256 debtRepaid, uint256 dynamicPenalty);

    // =============================================================================
    // ERRORS
    // =============================================================================

    error AssetNotSupported();
    error InsufficientBalance();
    error InsufficientCollateral();
    error HealthyPosition();
    error GracePeriodActive();
    error InvalidCurveParameters();
    error ZeroAmount();
    error PriceFeedUnavailable();

    // =============================================================================
    // CONSTRUCTOR
    // =============================================================================

    constructor(address _oraklRouter, address _feeRecipient) Ownable(msg.sender) {
        oraklRouter = IFeedRouter(_oraklRouter);
        feeRecipient = _feeRecipient;
    }

    // =============================================================================
    // BONDING CURVE MATHEMATICS
    // =============================================================================

    /**
     * @notice Calculate exponential interest rate using bonding curve
     * @param asset The asset to calculate rate for
     * @return interestRate Current interest rate based on utilization curve
     */
    function calculateBondingCurveInterestRate(address asset) public view returns (uint256 interestRate) {
        AssetConfig storage config = assets[asset];
        BondingCurveConfig storage curve = config.bondingCurve;

        if (config.totalDeposits == 0) return curve.baseRate;

        // Calculate current utilization (scaled by PRECISION)
        uint256 utilization = (config.totalBorrows * PRECISION) / config.totalDeposits;

        if (utilization <= curve.optimalUtilization) {
            // Below optimal: Linear increase
            uint256 utilizationRatio = (utilization * PRECISION) / curve.optimalUtilization;
            return curve.baseRate + ((curve.baseRate * utilizationRatio) / PRECISION);
        } else {
            // Above optimal: Exponential increase using bonding curve
            uint256 excessUtilization = utilization - curve.optimalUtilization;
            uint256 maxExcess = PRECISION - curve.optimalUtilization;

            // Exponential bonding curve: rate = baseRate * (1 + excess^exponent)
            uint256 excessRatio = (excessUtilization * PRECISION) / maxExcess;
            uint256 exponentialFactor = _exponentialCurve(excessRatio, curve.curveExponent);

            uint256 calculatedRate = curve.baseRate + 
                ((curve.maxRate - curve.baseRate) * exponentialFactor) / PRECISION;

            return calculatedRate > curve.maxRate ? curve.maxRate : calculatedRate;
        }
    }

    /**
     * @notice Calculate dynamic liquidation penalty using progressive bonding curve
     * @param borrower The borrower's address
     * @param healthFactor The position's health factor
     * @return penalty Dynamic penalty percentage
     */
    function calculateBondingCurveLiquidationPenalty(
        address borrower, 
        uint256 healthFactor
    ) public view returns (uint256 penalty) {
        // Get borrower's primary collateral asset for curve parameters
        address primaryAsset = _getPrimaryCollateralAsset(borrower);
        if (primaryAsset == address(0)) return 1000; // Default 10%

        BondingCurveConfig storage curve = assets[primaryAsset].bondingCurve;
        UserPosition storage position = userPositions[borrower];

        // Health factor curve: worse health = higher penalty
        // healthFactor < 1e18 means liquidatable
        uint256 healthRisk = healthFactor < PRECISION ? 
            (PRECISION - healthFactor) : 0;

        // Risk score curve: more liquidations = higher penalty
        uint256 riskMultiplier = PRECISION + (position.totalLiquidations * curve.penaltyCurveSlope);

        // Calculate penalty using bonding curve
        uint256 basePenalty = curve.minPenalty + 
            ((curve.maxPenalty - curve.minPenalty) * healthRisk) / PRECISION;

        penalty = (basePenalty * riskMultiplier) / PRECISION;

        // Cap at maximum penalty
        return penalty > curve.maxPenalty ? curve.maxPenalty : penalty;
    }

    /**
     * @notice Calculate collateral haircut based on volatility bonding curve
     * @param asset The collateral asset
     * @return haircut Dynamic haircut percentage
     */
    function calculateCollateralHaircut(address asset) public view returns (uint256 haircut) {
        AssetConfig storage config = assets[asset];
        BondingCurveConfig storage curve = config.bondingCurve;

        // Volatility-based haircut using bonding curve
        uint256 volatilityMultiplier = (config.avgVolatility * curve.volatilityFactor) / PRECISION;
        haircut = curve.baseHaircut + volatilityMultiplier;

        return haircut > curve.maxHaircut ? curve.maxHaircut : haircut;
    }

    /**
     * @notice Calculate time-decayed LTV using exponential decay curve
     * @param user The user's address
     * @param asset The collateral asset
     * @return effectiveLTV Time-adjusted LTV
     */
    function calculateTimeBondingCurveLTV(address user, address asset) public view returns (uint256 effectiveLTV) {
        AssetConfig storage config = assets[asset];
        BondingCurveConfig storage curve = config.bondingCurve;
        UserPosition storage position = userPositions[user];

        uint256 timeSinceOpen = block.timestamp - position.positionOpenTime[asset];
        if (timeSinceOpen == 0) return config.baseLTV;

        // Time decay bonding curve: LTV decreases over time for safety
        uint256 decayPeriod = timeSinceOpen > curve.maxDecayPeriod ? 
            curve.maxDecayPeriod : timeSinceOpen;

        uint256 decayFactor = (decayPeriod * curve.ltvDecayRate) / curve.maxDecayPeriod;
        uint256 ltvReduction = (config.baseLTV * decayFactor) / PRECISION;

        effectiveLTV = config.baseLTV > ltvReduction ? 
            config.baseLTV - ltvReduction : config.baseLTV / 2; // Minimum 50% of base LTV
    }

    /**
     * @notice Exponential curve helper function
     * @param x Input value (scaled by PRECISION)
     * @param exponent Curve exponent (scaled by PRECISION)
     * @return result Exponential result (scaled by PRECISION)
     */
    function _exponentialCurve(uint256 x, uint256 exponent) internal pure returns (uint256 result) {
        if (x == 0) return 0;
        if (x >= PRECISION) return PRECISION;

        // Simplified exponential approximation: x^n ≈ 1 + n*x (for small x)
        // For larger x, use: x^2, x^3 approximations
        if (exponent == PRECISION) return x; // Linear
        if (exponent == 2 * PRECISION) return (x * x) / PRECISION; // Quadratic

        // General exponential approximation using Taylor series
        uint256 term = x;
        result = PRECISION + term;

        for (uint256 i = 2; i <= 4 && term > 0; i++) {
            term = (term * x * exponent) / (PRECISION * PRECISION * i);
            result += term;
        }

        return result > PRECISION ? PRECISION : result;
    }

    /**
     * @notice Update volatility metrics for bonding curve calculations
     * @param asset The asset to update
     */
    function updateVolatilityMetrics(address asset) public {
        AssetConfig storage config = assets[asset];

        uint256 currentPrice = getAssetPrice(asset);
        uint256 timeDiff = block.timestamp - config.lastPriceUpdate;

        if (config.lastPriceUpdate > 0 && timeDiff > 0) {
            // Simple volatility calculation: |current - previous| / previous
            uint256 priceChange = currentPrice > config.priceHistory ? 
                currentPrice - config.priceHistory : 
                config.priceHistory - currentPrice;

            uint256 volatility = (priceChange * PRECISION) / config.priceHistory;

            // Update rolling average (simple moving average)
            config.avgVolatility = (config.avgVolatility * 9 + volatility) / 10;
        }

        config.priceHistory = currentPrice;
        config.lastPriceUpdate = block.timestamp;

        emit CollateralHaircutApplied(asset, config.avgVolatility, calculateCollateralHaircut(asset));
    }

    // =============================================================================
    // ASSET MANAGEMENT WITH BONDING CURVES
    // =============================================================================

    /**
     * @notice Add asset with comprehensive bonding curve configuration
     */
    function addAssetWithBondingCurves(
        address asset,
        uint256 baseLTV,
        uint256 liquidationThreshold,
        string memory oraklFeedName,
        bool canBorrow,
        bool canCollateralize,
        BondingCurveConfig memory bondingCurveConfig
    ) external onlyOwner {
        require(baseLTV <= liquidationThreshold, "Invalid LTV");
        require(liquidationThreshold <= 9500, "Threshold too high");

        // Validate bonding curve parameters
        require(bondingCurveConfig.baseRate <= bondingCurveConfig.maxRate, "Invalid rate curve");
        require(bondingCurveConfig.minPenalty <= bondingCurveConfig.maxPenalty, "Invalid penalty curve");
        require(bondingCurveConfig.baseHaircut <= bondingCurveConfig.maxHaircut, "Invalid haircut curve");

        assets[asset] = AssetConfig({
            isSupported: true,
            canBorrow: canBorrow,
            canCollateralize: canCollateralize,
            baseLTV: baseLTV,
            liquidationThreshold: liquidationThreshold,
            oraklFeedName: oraklFeedName,
            lastInterestUpdate: block.timestamp,
            totalDeposits: 0,
            totalBorrows: 0,
            accruedInterest: 0,
            reserveFactor: 1000, // 10%
            bondingCurve: bondingCurveConfig,
            avgVolatility: PRECISION / 10, // 10% initial volatility
            lastPriceUpdate: block.timestamp,
            priceHistory: 1e8, // Default 1 USD (8 decimals)
            fallbackPrice: 1e8 // Default 1 USD (8 decimals)
        });

        supportedAssets.push(asset);
        emit AssetAdded(asset, bondingCurveConfig);
    }

    // =============================================================================
    // ENHANCED LENDING FUNCTIONS
    // =============================================================================

    /**
     * @notice Deposit with bonding curve interest calculation
     */
    function deposit(address asset, uint256 amount) external nonReentrant {
        if (amount == 0) revert ZeroAmount();
        AssetConfig storage config = assets[asset];
        if (!config.isSupported) revert AssetNotSupported();

        updateInterestWithBondingCurve(asset);
        updateVolatilityMetrics(asset);

        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);

        userPositions[msg.sender].deposits[asset] += amount;
        if (userPositions[msg.sender].positionOpenTime[asset] == 0) {
            userPositions[msg.sender].positionOpenTime[asset] = block.timestamp;
        }

        config.totalDeposits += amount;

        emit Deposited(msg.sender, asset, amount, userPositions[msg.sender].deposits[asset]);
    }

    /**
     * @notice Borrow with dynamic LTV from bonding curves
     */
    function borrow(address asset, uint256 amount) external nonReentrant {
        if (amount == 0) revert ZeroAmount();
        AssetConfig storage config = assets[asset];
        if (!config.isSupported || !config.canBorrow) revert AssetNotSupported();

        updateInterestWithBondingCurve(asset);
        updateVolatilityMetrics(asset);

        // Calculate effective collateral with bonding curve adjustments
        uint256 effectiveCollateralUSD = getEffectiveCollateralUSD(msg.sender);
        uint256 totalBorrowUSD = getTotalBorrowUSD(msg.sender);
        uint256 borrowAmountUSD = getUSDValue(asset, amount);

        uint256 newBorrowUSD = totalBorrowUSD + borrowAmountUSD;

        if (newBorrowUSD > effectiveCollateralUSD) revert InsufficientCollateral();
        if (amount > IERC20(asset).balanceOf(address(this))) revert InsufficientBalance();

        userPositions[msg.sender].borrows[asset] += amount;
        userPositions[msg.sender].lastBorrowUpdate[asset] = block.timestamp;
        config.totalBorrows += amount;

        // Calculate effective LTV for this borrow
        uint256 effectiveLTV = effectiveCollateralUSD > 0 ? 
            (newBorrowUSD * BASIS_POINTS) / effectiveCollateralUSD : 0;

        IERC20(asset).safeTransfer(msg.sender, amount);

        emit Borrowed(msg.sender, asset, amount, effectiveLTV);
    }

    /**
     * @notice Update interest using bonding curve calculations
     */
    function updateInterestWithBondingCurve(address asset) public {
        AssetConfig storage config = assets[asset];
        if (!config.isSupported) revert AssetNotSupported();

        uint256 timeElapsed = block.timestamp - config.lastInterestUpdate;
        if (timeElapsed == 0) return;

        uint256 currentRate = calculateBondingCurveInterestRate(asset);
        uint256 interestAccrued = (config.totalBorrows * currentRate * timeElapsed) / 
                                 (SECONDS_PER_YEAR * BASIS_POINTS);

        config.accruedInterest += interestAccrued;
        config.lastInterestUpdate = block.timestamp;

        // Calculate utilization for event
        uint256 utilization = config.totalDeposits == 0 ? 0 : 
            (config.totalBorrows * BASIS_POINTS) / config.totalDeposits;

        emit DynamicRateCalculated(asset, utilization, currentRate, 
            config.totalDeposits > 0 ? (config.totalBorrows * PRECISION) / config.totalDeposits : 0);
    }

    /**
     * @notice Enhanced liquidation with dynamic bonding curve penalties
     */
    function liquidateWithBondingCurve(
        address borrower,
        address collateralAsset,
        uint256 repayAmount
    ) external nonReentrant {
        (bool isLiquidatable, uint256 healthFactor) = checkLiquidation(borrower);
        if (!isLiquidatable) revert HealthyPosition();

        UserPosition storage position = userPositions[borrower];
        require(!position.inGracePeriod || 
                block.timestamp > position.gracePeriodEnd, "Grace period active");

        // Calculate dynamic penalty using bonding curve
        uint256 dynamicPenalty = calculateBondingCurveLiquidationPenalty(borrower, healthFactor);

        uint256 collateralPrice = getAssetPrice(collateralAsset);
        uint256 debtValue = getUSDValue(collateralAsset, repayAmount);
        uint256 collateralToSeize = (debtValue * (BASIS_POINTS + dynamicPenalty)) / 
                                   (collateralPrice * BASIS_POINTS);

        require(collateralToSeize <= position.deposits[collateralAsset], 
                "Insufficient collateral");

        IERC20(collateralAsset).safeTransferFrom(msg.sender, address(this), repayAmount);

        position.deposits[collateralAsset] -= collateralToSeize;
        position.borrows[collateralAsset] -= repayAmount;
        position.totalLiquidations += 1; // Update for future penalty calculations

        IERC20(collateralAsset).safeTransfer(msg.sender, collateralToSeize);

        emit LiquidationPenaltyCalculated(borrower, healthFactor, dynamicPenalty);
        emit Liquidated(borrower, msg.sender, collateralAsset, 
                       collateralToSeize, repayAmount, dynamicPenalty);
    }

    // =============================================================================
    // ENHANCED VIEW FUNCTIONS
    // =============================================================================

    /**
     * @notice Get effective collateral USD value with bonding curve adjustments
     */
    function getEffectiveCollateralUSD(address user) public view returns (uint256 totalUSD) {
        for (uint256 i = 0; i < supportedAssets.length; i++) {
            address asset = supportedAssets[i];
            if (assets[asset].canCollateralize) {
                uint256 balance = userPositions[user].deposits[asset];
                if (balance > 0) {
                    uint256 valueUSD = getUSDValue(asset, balance);
                    uint256 effectiveLTV = calculateTimeBondingCurveLTV(user, asset);
                    uint256 haircut = calculateCollateralHaircut(asset);

                    // Apply bonding curve adjustments
                    uint256 adjustedValue = (valueUSD * effectiveLTV * (BASIS_POINTS - haircut)) / 
                                          (BASIS_POINTS * BASIS_POINTS);
                    totalUSD += adjustedValue;
                }
            }
        }
    }

    /**
     * @notice Get comprehensive bonding curve metrics for an asset
     */
    function getBondingCurveMetrics(address asset) external view returns (
        uint256 currentRate,
        uint256 utilization,
        uint256 avgVolatility,
        uint256 collateralHaircut,
        uint256 curveValue
    ) {
        AssetConfig storage config = assets[asset];

        currentRate = calculateBondingCurveInterestRate(asset);
        utilization = config.totalDeposits == 0 ? 0 : 
            (config.totalBorrows * BASIS_POINTS) / config.totalDeposits;
        avgVolatility = config.avgVolatility;
        collateralHaircut = calculateCollateralHaircut(asset);
        curveValue = config.totalDeposits > 0 ? 
            (config.totalBorrows * PRECISION) / config.totalDeposits : 0;
    }

    /**
     * @notice Get user's position with bonding curve adjustments
     */
    function getEnhancedUserPosition(address user) external view returns (
        uint256 totalCollateralUSD,
        uint256 effectiveCollateralUSD,
        uint256 totalBorrowUSD,
        uint256 healthFactor,
        uint256 riskScore,
        bool inGracePeriod
    ) {
        totalCollateralUSD = getTotalCollateralUSD(user);
        effectiveCollateralUSD = getEffectiveCollateralUSD(user);
        totalBorrowUSD = getTotalBorrowUSD(user);

        if (totalBorrowUSD == 0) {
            healthFactor = type(uint256).max;
        } else {
            healthFactor = (effectiveCollateralUSD * PRECISION) / totalBorrowUSD;
        }

        riskScore = userPositions[user].riskScore;
        inGracePeriod = userPositions[user].inGracePeriod;
    }

    // =============================================================================
    // UTILITY FUNCTIONS
    // =============================================================================

    function getTotalCollateralUSD(address user) public view returns (uint256 totalUSD) {
        for (uint256 i = 0; i < supportedAssets.length; i++) {
            address asset = supportedAssets[i];
            if (assets[asset].canCollateralize) {
                uint256 balance = userPositions[user].deposits[asset];
                if (balance > 0) {
                    totalUSD += getUSDValue(asset, balance);
                }
            }
        }
    }

    function getTotalBorrowUSD(address user) public view returns (uint256 totalUSD) {
        for (uint256 i = 0; i < supportedAssets.length; i++) {
            address asset = supportedAssets[i];
            uint256 balance = userPositions[user].borrows[asset];
            if (balance > 0) {
                totalUSD += getUSDValue(asset, balance);
            }
        }
    }

    function getAssetPrice(address asset) public view returns (uint256 price) {
        AssetConfig storage config = assets[asset];
        if (!config.isSupported) revert AssetNotSupported();

        address feedProxy = oraklRouter.feedToProxies(config.oraklFeedName);
        if (feedProxy == address(0)) {
            return config.fallbackPrice;
        }

        try IFeed(feedProxy).latestRoundData() returns (
            uint64, int256 answer, uint256
        ) {
            require(answer > 0, "Invalid price");
            return uint256(answer);
        } catch {
            revert PriceFeedUnavailable();
        }
    }

    function getUSDValue(address asset, uint256 amount) public view returns (uint256 usdValue) {
        uint256 price = getAssetPrice(asset);
        uint256 decimals = IERC20Metadata(asset).decimals();
        return (amount * price) / (10 ** decimals);
    }

    function checkLiquidation(address borrower) public view returns (bool isLiquidatable, uint256 healthFactor) {
        uint256 effectiveCollateralUSD = getEffectiveCollateralUSD(borrower);
        uint256 totalBorrowUSD = getTotalBorrowUSD(borrower);

        if (totalBorrowUSD == 0) return (false, type(uint256).max);

        healthFactor = (effectiveCollateralUSD * PRECISION) / totalBorrowUSD;
        isLiquidatable = healthFactor < PRECISION;
    }

    function _getPrimaryCollateralAsset(address user) internal view returns (address primaryAsset) {
        uint256 maxValue = 0;

        for (uint256 i = 0; i < supportedAssets.length; i++) {
            address asset = supportedAssets[i];
            if (assets[asset].canCollateralize) {
                uint256 balance = userPositions[user].deposits[asset];
                if (balance > 0) {
                    uint256 valueUSD = getUSDValue(asset, balance);
                    if (valueUSD > maxValue) {
                        maxValue = valueUSD;
                        primaryAsset = asset;
                    }
                }
            }
        }
    }

    // Standard functions (repay, withdraw, claimInterest) remain the same...
    function repay(address asset, uint256 amount) external nonReentrant {
        if (amount == 0) revert ZeroAmount();

        updateInterestWithBondingCurve(asset);

        uint256 userDebt = userPositions[msg.sender].borrows[asset];
        if (userDebt == 0) revert InsufficientBalance();

        uint256 repayAmount = amount > userDebt ? userDebt : amount;

        IERC20(asset).safeTransferFrom(msg.sender, address(this), repayAmount);

        userPositions[msg.sender].borrows[asset] -= repayAmount;
        assets[asset].totalBorrows -= repayAmount;

        if (userPositions[msg.sender].borrows[asset] == 0 && 
            userPositions[msg.sender].inGracePeriod) {
            userPositions[msg.sender].inGracePeriod = false;
            userPositions[msg.sender].gracePeriodEnd = 0;
        }

        emit Repaid(msg.sender, asset, repayAmount, userPositions[msg.sender].borrows[asset]);
    }

    function withdraw(address asset, uint256 amount) external nonReentrant {
        if (amount == 0) revert ZeroAmount();

        updateInterestWithBondingCurve(asset);

        uint256 userBalance = userPositions[msg.sender].deposits[asset];
        if (amount > userBalance) revert InsufficientBalance();

        // Check collateralization with bonding curve adjustments
        uint256 newEffectiveCollateralUSD = getEffectiveCollateralUSD(msg.sender) - 
            ((getUSDValue(asset, amount) * calculateTimeBondingCurveLTV(msg.sender, asset) * 
              (BASIS_POINTS - calculateCollateralHaircut(asset))) / (BASIS_POINTS * BASIS_POINTS));
        uint256 totalBorrowUSD = getTotalBorrowUSD(msg.sender);

        if (totalBorrowUSD > 0 && totalBorrowUSD > newEffectiveCollateralUSD) {
            revert InsufficientCollateral();
        }

        userPositions[msg.sender].deposits[asset] -= amount;
        assets[asset].totalDeposits -= amount;

        IERC20(asset).safeTransfer(msg.sender, amount);

        emit Withdrawn(msg.sender, asset, amount, userPositions[msg.sender].deposits[asset]);
    }

    // =============================================================================
    // ADMIN FUNCTIONS
    // =============================================================================

    function updateBondingCurveParameter(
        address asset,
        string memory parameterType,
        uint256 newValue
    ) external onlyOwner {
        BondingCurveConfig storage curve = assets[asset].bondingCurve;

        if (keccak256(bytes(parameterType)) == keccak256("baseRate")) {
            curve.baseRate = newValue;
        } else if (keccak256(bytes(parameterType)) == keccak256("maxRate")) {
            curve.maxRate = newValue;
        } else if (keccak256(bytes(parameterType)) == keccak256("curveExponent")) {
            curve.curveExponent = newValue;
        } else if (keccak256(bytes(parameterType)) == keccak256("minPenalty")) {
            curve.minPenalty = newValue;
        } else if (keccak256(bytes(parameterType)) == keccak256("maxPenalty")) {
            curve.maxPenalty = newValue;
        }

        emit BondingCurveUpdated(asset, parameterType, newValue);
    }
}

