        bytes memory output = new bytes(2 * buffer.length); // worst case scenario
        uint256 outputLength = 0;

        for (uint256 i; i < buffer.length; ++i) {
            bytes1 char = bytes1(_unsafeReadBytesOffset(buffer, i));
            if (((SPECIAL_CHARS_LOOKUP & (1 << uint8(char))) != 0)) {
                output[outputLength++] = "\\";
                if (char == 0x08) output[outputLength++] = "b";
                else if (char == 0x09) output[outputLength++] = "t";
                else if (char == 0x0a) output[outputLength++] = "n";
                else if (char == 0x0c) output[outputLength++] = "f";
                else if (char == 0x0d) output[outputLength++] = "r";
                else if (char == 0x5c) output[outputLength++] = "\\";
                else if (char == 0x22) {
                    // solhint-disable-next-line quotes
                    output[outputLength++] = '"';
                }
            } else {
                output[outputLength++] = char;
            }
        }
        // write the actual length and deallocate unused memory
        assembly ("memory-safe") {
            mstore(output, outputLength)
            mstore(0x40, add(output, shl(5, shr(5, add(outputLength, 63)))))
        }

        return string(output);
    }

    /**
     * @dev Reads a bytes32 from a bytes array without bounds checking.
     *
     * NOTE: making this function internal would mean it could be used with memory unsafe offset, and marking the
     * assembly block as such would prevent some optimizations.
     */
    function _unsafeReadBytesOffset(bytes memory buffer, uint256 offset) private pure returns (bytes32 value) {
        // This is not memory safe in the general case, but all calls to this private function are within bounds.
        assembly ("memory-safe") {
            value := mload(add(add(buffer, 0x20), offset))
        }
    }
}

contracts/BulkPayroll.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title BulkPayroll
 * @dev Enhanced bulk payment contract with failure isolation
 * @notice Handles bulk ETH and ERC20 transfers with individual failure handling
 */
contract BulkPayroll is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    constructor() Ownable(msg.sender) {}

    /// @dev Maximum recipients per batch to prevent gas limit issues
    uint256 public constant MAX_RECIPIENTS = 200;

    /// @dev Gas limit for ETH transfers to prevent griefing
    uint256 public constant GAS_LIMIT = 50000;

    /// @dev Mapping for failed transfers: recipient => token => amount
    mapping(address => mapping(address => uint256)) public failedTransfers;

    /// @dev Custom errors
    error LengthMismatch();
    error InsufficientBalance();
    error InsufficientAllowance();
    error TransferError();
    error RefundFailed();
    error TooManyRecipients();
    error InvalidInput();

    /// @dev Events
    event BulkTransferExecuted(
        address indexed sender,
        address indexed token,
        uint256 totalRecipients,
        uint256 totalAmount,
        uint256 successCount,
        uint256 failCount
    );

    event TransferFailed(
        address indexed recipient,
        address indexed token,
        uint256 amount
    );

    event FailedTransferClaimed(
        address indexed recipient,
        address token,
        uint256 amount
    );

    /**
     * @notice Executes a bulk transfer of tokens/Native currency with failure isolation
     * @dev Failed transfers are stored for later claiming
     * @param token ERC20 token address or address(0) for Native currency
     * @param recipients Array of recipient addresses
     * @param amounts Array of corresponding transfer amounts
     */
    function bulkTransfer(
        address token,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) external payable nonReentrant {
        uint256 length = recipients.length;

        // Input validation
        if (length == 0) revert InvalidInput();
        if (length > MAX_RECIPIENTS) revert TooManyRecipients();
        if (length != amounts.length) revert LengthMismatch();

        uint256 totalAmount;
        for (uint256 i; i < length;) {
            if (recipients[i] == address(0) || amounts[i] == 0) revert InvalidInput();
            totalAmount += amounts[i];
            unchecked { ++i; }
        }

        if (token == address(0)) {
            _handleNativeTransfer(totalAmount, recipients, amounts);
        } else {
            _handleERC20Transfer(token, totalAmount, recipients, amounts);
        }
    }

    /**
     * @notice Claim failed transfers
     * @param token Token to claim (address(0) for ETH)
     */
    function claimFailedTransfer(address token) external nonReentrant {
        uint256 amount = failedTransfers[msg.sender][token];
        if (amount == 0) revert InvalidInput();

        failedTransfers[msg.sender][token] = 0;

        bool success = _attemptTransfer(msg.sender, token, amount);

        if (success) {
            emit FailedTransferClaimed(msg.sender, token, amount);
        } else {
            failedTransfers[msg.sender][token] = amount; // Restore balance
            revert TransferError();
        }
    }

    /**
     * @dev Handle native currency transfers with failure isolation
     */
    function _handleNativeTransfer(
        uint256 totalAmount,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) private {
        if (msg.value < totalAmount) revert InsufficientBalance();

        uint256 successCount = 0;
        uint256 failCount = 0;

        for (uint256 i; i < recipients.length;) {
            if (_attemptTransfer(recipients[i], address(0), amounts[i])) {
                successCount++;
            } else {
                failCount++;
                failedTransfers[recipients[i]][address(0)] += amounts[i];
                emit TransferFailed(recipients[i], address(0), amounts[i]);
            }
            unchecked { ++i; }
        }

        emit BulkTransferExecuted(msg.sender, address(0), recipients.length, totalAmount, successCount, failCount);
        _refundExcessNative(totalAmount);
    }

    /**
     * @dev Handle ERC20 transfers with failure isolation
     */
    function _handleERC20Transfer(
        address token,
        uint256 totalAmount,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) private {
        IERC20 tokenContract = IERC20(token);
        uint256 allowance = tokenContract.allowance(msg.sender, address(this));
        if (allowance < totalAmount) revert InsufficientAllowance();

        // Transfer total amount to contract first
        tokenContract.safeTransferFrom(msg.sender, address(this), totalAmount);

        uint256 successCount = 0;
        uint256 failCount = 0;

        for (uint256 i; i < recipients.length;) {
            if (_attemptTransfer(recipients[i], token, amounts[i])) {
                successCount++;
            } else {
                failCount++;
                failedTransfers[recipients[i]][token] += amounts[i];
                emit TransferFailed(recipients[i], token, amounts[i]);
            }
            unchecked { ++i; }
        }

        emit BulkTransferExecuted(msg.sender, token, recipients.length, totalAmount, successCount, failCount);
    }

    /**
     * @dev Attempt transfer with gas limit protection
     */
    function _attemptTransfer(
        address recipient,
        address token,
        uint256 amount
    ) internal returns (bool success) {
        if (token == address(0)) {
            (success, ) = recipient.call{value: amount, gas: GAS_LIMIT}("");
        } else {
            try IERC20(token).transfer(recipient, amount) {
                success = true;
            } catch {
                success = false;
            }
        }
    }

    /**
     * @dev Refund excess native currency
     */
    function _refundExcessNative(uint256 totalAmount) private {
        uint256 remainder = msg.value - totalAmount;
        if (remainder > 0) {
            (bool success,) = msg.sender.call{value: remainder, gas: GAS_LIMIT}("");
            if (!success) revert RefundFailed();
        }
    }

    /**
     * @notice Get failed transfer amount for a recipient
     */
    function getFailedAmount(address recipient, address token) external view returns (uint256) {
        return failedTransfers[recipient][token];
    }

    /**
     * @notice Emergency withdrawal for owner (only failed transfers)
     */
    function emergencyWithdraw(address token) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(address(this).balance);
        } else {
            IERC20(token).safeTransfer(owner(), IERC20(token).balanceOf(address(this)));
        }
    }

    // Accept ETH
    receive() external payable {}
}

contracts/DummyUSDT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title DummyUSDT
 * @dev A simple ERC20 token for testing purposes with faucet functionality
 * @notice Compatible with Kaia testnet, includes built-in faucet for easy testing
 */
contract DummyUSDT is ERC20, Ownable {

    /// @dev Faucet configuration
    uint256 public constant FAUCET_AMOUNT = 1000 * 10**18; // 1000 tokens
    uint256 public constant FAUCET_COOLDOWN = 24 hours;

    /// @dev Mapping to track last faucet claim time
    mapping(address => uint256) public lastFaucetClaim;

    /// @dev Events
    event FaucetClaimed(address indexed user, uint256 amount);
    event FaucetFunded(uint256 amount);

    /// @dev Custom errors
    error FaucetCooldown();
    error FaucetEmpty();

    /**
     * @dev Constructor - creates initial supply and sets up faucet
     * @param initialSupply Initial token supply (without decimals)
     */
    constructor(uint256 initialSupply) ERC20("Dummy USDT", "DUSDT") Ownable(msg.sender) {
        // Mint initial supply to owner
        _mint(msg.sender, initialSupply * 10**decimals());

        // Fund faucet with 10% of initial supply
        uint256 faucetFunding = (initialSupply * 10**decimals()) / 10;
        _mint(address(this), faucetFunding);

        emit FaucetFunded(faucetFunding);
    }

    /**
     * @notice Claim tokens from faucet (once per 24 hours)
     * @dev Anyone can call this to get test tokens
     */
    function faucet() external {
        // Check cooldown
        if (block.timestamp < lastFaucetClaim[msg.sender] + FAUCET_COOLDOWN) {
            revert FaucetCooldown();
        }

        // Check faucet balance
        if (balanceOf(address(this)) < FAUCET_AMOUNT) {
            revert FaucetEmpty();
        }

        // Update claim time before transfer (reentrancy protection)
        lastFaucetClaim[msg.sender] = block.timestamp;

        // Transfer tokens
        _transfer(address(this), msg.sender, FAUCET_AMOUNT);

        emit FaucetClaimed(msg.sender, FAUCET_AMOUNT);
    }

    /**
     * @notice Mint new tokens (only owner)
     * @param to Address to mint tokens to
     * @param amount Amount to mint (with decimals)
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @notice Fund the faucet (only owner)
     * @param amount Amount to fund faucet with
     */
    function fundFaucet(uint256 amount) external onlyOwner {
        _transfer(msg.sender, address(this), amount);
        emit FaucetFunded(amount);
    }

    /**
     * @notice Get faucet balance
     */
    function getFaucetBalance() external view returns (uint256) {
        return balanceOf(address(this));
    }

    /**
     * @notice Get time until next faucet claim for user
     * @param user Address to check
     */
    function getTimeUntilNextClaim(address user) external view returns (uint256) {
        uint256 nextClaim = lastFaucetClaim[user] + FAUCET_COOLDOWN;
        if (block.timestamp >= nextClaim) {
            return 0;
        }
        return nextClaim - block.timestamp;
    }

    /**
     * @notice Check if user can claim from faucet
     * @param user Address to check
     */
    function canClaimFaucet(address user) external view returns (bool) {
        return block.timestamp >= lastFaucetClaim[user] + FAUCET_COOLDOWN 
               && balanceOf(address(this)) >= FAUCET_AMOUNT;
    }

    /**
     * @notice Emergency withdrawal of faucet funds (only owner)
     */
    function withdrawFaucetFunds() external onlyOwner {
        uint256 balance = balanceOf(address(this));
        if (balance > 0) {
            _transfer(address(this), owner(), balance);
        }
    }
}

contracts/InvoiceSubscription.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

// Chainlink Automation interface
interface AutomationCompatibleInterface {
    function checkUpkeep(bytes calldata checkData) external view returns (bool upkeepNeeded, bytes memory performData);
    function performUpkeep(bytes calldata performData) external;
}

/**
 * @title InvoiceSubscription
 * @dev Enhanced contract for invoice payments and subscription management with Chainlink Automation
 * @notice Handles one-time invoices and subscription-based payments via EIP-712 signatures
 * Features automatic escrow release via Chainlink Automation when invoices are fully funded
 */
contract InvoiceSubscription is ReentrancyGuard, Ownable, Pausable, EIP712, AutomationCompatibleInterface {
    using SafeERC20 for IERC20;
    using ECDSA for bytes32;

    /// @dev Invoice structure
    struct Invoice {
        address seller;
        uint256 invoiceAmount;
        address token; // address(0) for ETH
        uint256 deadline;
        uint256 totalContributed;
        bool released;
        bool refunded;
        mapping(address => uint256) contributions;
        address[] contributors;
    }

    /// @dev Subscription consent structure for EIP-712
    struct SubscriptionConsent {
        address buyer;
        address seller;
        address token;
        uint256 amountPerInterval;
        uint256 intervalSeconds;
        uint256 startTimestamp;
        uint256 endTimestamp;
        uint256 nonce;
    }

    /// @dev Active subscription tracking
    struct ActiveSubscription {
        address buyer;
        address seller;
        address token;
        uint256 amountPerInterval;
        uint256 intervalSeconds;
        uint256 nextDue;
        uint256 endTimestamp;
        bool active;
        uint256 totalPaid;
    }

    /// @dev EIP-712 type hash for subscription consent
    bytes32 public constant SUBSCRIPTION_CONSENT_TYPEHASH = keccak256(
        "SubscriptionConsent(address buyer,address seller,address token,uint256 amountPerInterval,uint256 intervalSeconds,uint256 startTimestamp,uint256 endTimestamp,uint256 nonce)"
    );

    /// @dev Mapping from invoice ID to invoice data
    mapping(bytes32 => Invoice) public invoices;

    /// @dev Mapping from consent nonce to used status
    mapping(address => mapping(uint256 => bool)) public usedNonces;

    /// @dev Mapping from subscription key to active subscription
    mapping(bytes32 => ActiveSubscription) public subscriptions;

    /// @dev Array to track all invoice IDs for automation checks
    bytes32[] public allInvoiceIds;

    /// @dev Mapping to check if invoice ID is already tracked
    mapping(bytes32 => bool) public invoiceTracked;

    /// @dev Custom errors
    error InvalidInvoice();
    error InvalidAmount();
    error InvoiceExpired();
    error InvoiceAlreadyReleased();
    error InvoiceNotExpired();
    error NoContribution();
    error TransferFailed();
    error InvalidSignature();
    error NonceUsed();
    error SubscriptionExpired();
    error SubscriptionNotDue();
    error InvalidConsent();

    /// @dev Events
    event InvoiceCreated(
        bytes32 indexed invoiceId,
        address indexed seller,
        uint256 invoiceAmount,
        address token,
        uint256 deadline
    );

    event InvoiceFunded(
        bytes32 indexed invoiceId,
        address indexed contributor,
        uint256 amount,
        uint256 totalContributed
    );

    event InvoiceReleased(
        bytes32 indexed invoiceId,
        address indexed seller,
        uint256 amount
    );

    event InvoiceRefunded(
        bytes32 indexed invoiceId,
        address indexed contributor,
        uint256 amount
    );

    event SubscriptionCreated(
        bytes32 indexed subscriptionKey,
        address indexed buyer,
        address indexed seller,
        uint256 amountPerInterval,
        uint256 intervalSeconds
    );

    event SubscriptionPayment(
        bytes32 indexed subscriptionKey,
        address indexed buyer,
        address indexed seller,
        uint256 amount,
        uint256 nextDue
    );

    event AutomationTriggered(
        bytes32 indexed invoiceId,
        uint256 timestamp
    );

    constructor() EIP712("InvoiceSubscription", "1") Ownable(msg.sender) {}

    /**
     * @notice Create a new invoice
     * @param seller Address that will receive the payment
     * @param invoiceAmount Total amount required
     * @param token Token address (address(0) for ETH)
     * @param deadline Payment deadline timestamp
     */
    function createInvoice(
        address seller,
        uint256 invoiceAmount,
        address token,
        uint256 deadline
    ) external whenNotPaused returns (bytes32 invoiceId) {
        if (seller == address(0) || invoiceAmount == 0) revert InvalidInvoice();
        if (deadline <= block.timestamp) revert InvalidInvoice();

        invoiceId = keccak256(abi.encodePacked(seller, invoiceAmount, token, deadline, block.timestamp));

        Invoice storage invoice = invoices[invoiceId];
        invoice.seller = seller;
        invoice.invoiceAmount = invoiceAmount;
        invoice.token = token;
        invoice.deadline = deadline;

        // Add to tracking for Chainlink Automation
        if (!invoiceTracked[invoiceId]) {
            allInvoiceIds.push(invoiceId);
            invoiceTracked[invoiceId] = true;
        }

        emit InvoiceCreated(invoiceId, seller, invoiceAmount, token, deadline);
    }

    /**
     * @notice Fund an invoice (partial or full payment)
     * @param invoiceId The invoice to fund
     */
    function fundInvoice(bytes32 invoiceId) external payable nonReentrant whenNotPaused {
        Invoice storage invoice = invoices[invoiceId];
        if (invoice.seller == address(0)) revert InvalidInvoice();
        if (invoice.released || invoice.refunded) revert InvoiceAlreadyReleased();
        if (block.timestamp > invoice.deadline) revert InvoiceExpired();

        uint256 amount;
        if (invoice.token == address(0)) {
            amount = msg.value;
        } else {
            amount = IERC20(invoice.token).allowance(msg.sender, address(this));
            IERC20(invoice.token).safeTransferFrom(msg.sender, address(this), amount);
        }

        if (amount == 0) revert InvalidAmount();

        // Update contribution tracking
        if (invoice.contributions[msg.sender] == 0) {
            invoice.contributors.push(msg.sender);
        }
        invoice.contributions[msg.sender] += amount;
        invoice.totalContributed += amount;

        emit InvoiceFunded(invoiceId, msg.sender, amount, invoice.totalContributed);

        // Auto-release if fully funded (backup to Chainlink Automation)
        if (invoice.totalContributed >= invoice.invoiceAmount) {
            _releaseInvoice(invoiceId);
        }
    }

    /**
     * @notice Create subscription via EIP-712 signature
     * @param consent The subscription consent data
     * @param signature The signed consent
     */
    function createSubscriptionWithConsent(
        SubscriptionConsent calldata consent,
        bytes calldata signature
    ) external nonReentrant whenNotPaused {
        // Verify signature
        bytes32 structHash = keccak256(abi.encode(
            SUBSCRIPTION_CONSENT_TYPEHASH,
            consent.buyer,
            consent.seller,
            consent.token,
            consent.amountPerInterval,
            consent.intervalSeconds,
            consent.startTimestamp,
            consent.endTimestamp,
            consent.nonce
        ));

        bytes32 hash = _hashTypedDataV4(structHash);
        address signer = hash.recover(signature);

        if (signer != consent.buyer) revert InvalidSignature();
        if (usedNonces[consent.buyer][consent.nonce]) revert NonceUsed();
        if (block.timestamp > consent.endTimestamp) revert SubscriptionExpired();

        // Mark nonce as used
        usedNonces[consent.buyer][consent.nonce] = true;

        // Create subscription
        bytes32 subscriptionKey = keccak256(abi.encodePacked(
            consent.buyer, 
            consent.seller, 
            consent.token,
            consent.nonce
        ));

        ActiveSubscription storage subscription = subscriptions[subscriptionKey];
        subscription.buyer = consent.buyer;
        subscription.seller = consent.seller;
        subscription.token = consent.token;
        subscription.amountPerInterval = consent.amountPerInterval;
        subscription.intervalSeconds = consent.intervalSeconds;
        subscription.nextDue = consent.startTimestamp;
        subscription.endTimestamp = consent.endTimestamp;
        subscription.active = true;

        emit SubscriptionCreated(
            subscriptionKey,
            consent.buyer,
            consent.seller,
            consent.amountPerInterval,
            consent.intervalSeconds
        );
    }

    /**
     * @notice Execute subscription payment
     * @param subscriptionKey The subscription to process
     */
    function pullSubscriptionPayment(bytes32 subscriptionKey) external payable nonReentrant whenNotPaused {
        ActiveSubscription storage subscription = subscriptions[subscriptionKey];

        if (!subscription.active) revert InvalidConsent();
        if (block.timestamp > subscription.endTimestamp) revert SubscriptionExpired();
        if (block.timestamp < subscription.nextDue) revert SubscriptionNotDue();

        // Process payment
        if (subscription.token == address(0)) {
            // ETH payment - buyer must send ETH with transaction
            if (msg.value < subscription.amountPerInterval) revert InvalidAmount();

            (bool success,) = subscription.seller.call{value: subscription.amountPerInterval}("");
            if (!success) revert TransferFailed();

            // Refund excess
            if (msg.value > subscription.amountPerInterval) {
                (bool refundSuccess,) = msg.sender.call{value: msg.value - subscription.amountPerInterval}("");
                if (!refundSuccess) revert TransferFailed();
            }
        } else {
            // ERC20 payment
            IERC20(subscription.token).safeTransferFrom(
                subscription.buyer,
                subscription.seller,
                subscription.amountPerInterval
            );
        }

        // Update subscription state
        subscription.nextDue += subscription.intervalSeconds;
        subscription.totalPaid += subscription.amountPerInterval;

        emit SubscriptionPayment(
            subscriptionKey,
            subscription.buyer,
            subscription.seller,
            subscription.amountPerInterval,
            subscription.nextDue
        );
    }

    /**
     * @notice Request refund for expired invoice
     * @param invoiceId The invoice to refund
     */
    function requestRefund(bytes32 invoiceId) external nonReentrant {
        Invoice storage invoice = invoices[invoiceId];
        if (invoice.seller == address(0)) revert InvalidInvoice();
        if (invoice.released || invoice.refunded) revert InvoiceAlreadyReleased();
        if (block.timestamp <= invoice.deadline) revert InvoiceNotExpired();

        uint256 contribution = invoice.contributions[msg.sender];
        if (contribution == 0) revert NoContribution();

        invoice.contributions[msg.sender] = 0;
        invoice.refunded = true;

        // Transfer refund
        if (invoice.token == address(0)) {
            (bool success,) = msg.sender.call{value: contribution}("");
            if (!success) revert TransferFailed();
        } else {
            IERC20(invoice.token).safeTransfer(msg.sender, contribution);
        }

        emit InvoiceRefunded(invoiceId, msg.sender, contribution);
    }

    /**
     * @dev Internal function to release invoice payment
     */
    function _releaseInvoice(bytes32 invoiceId) internal {
        Invoice storage invoice = invoices[invoiceId];
        if (invoice.released || invoice.refunded) return;

        invoice.released = true;

        // Transfer payment to seller
        if (invoice.token == address(0)) {
            (bool success,) = invoice.seller.call{value: invoice.totalContributed}("");
            if (!success) revert TransferFailed();
        } else {
            IERC20(invoice.token).safeTransfer(invoice.seller, invoice.totalContributed);
        }

        emit InvoiceReleased(invoiceId, invoice.seller, invoice.totalContributed);
    }

    /**
     * @notice Chainlink Automation: Check if any invoices need to be automatically released
     * @param checkData Additional data (not used in this implementation)
     * @return upkeepNeeded True if upkeep is needed
     * @return performData Encoded invoice IDs that need to be released
     */
    function checkUpkeep(bytes calldata checkData) 
        external 
        view 
        override 
        returns (bool upkeepNeeded, bytes memory performData) 
    {
        checkData; // Silence unused parameter warning

        bytes32[] memory invoicesToRelease = new bytes32[](allInvoiceIds.length);
        uint256 count = 0;

        // Check all invoices to see which ones are ready for release
        for (uint256 i = 0; i < allInvoiceIds.length; i++) {
            bytes32 invoiceId = allInvoiceIds[i];
            Invoice storage invoice = invoices[invoiceId];

            // Check if invoice is fully funded but not yet released
            if (!invoice.released && 
                !invoice.refunded && 
                invoice.totalContributed >= invoice.invoiceAmount &&
                block.timestamp <= invoice.deadline) {
                invoicesToRelease[count] = invoiceId;
                count++;
            }
        }

        // If we found invoices to release, return true with the data
        if (count > 0) {
            upkeepNeeded = true;

            // Create array with only the invoices that need release
            bytes32[] memory finalInvoices = new bytes32[](count);
            for (uint256 i = 0; i < count; i++) {
                finalInvoices[i] = invoicesToRelease[i];
            }

            performData = abi.encode(finalInvoices);
        } else {
            upkeepNeeded = false;
            performData = "";
        }
    }

    /**
     * @notice Chainlink Automation: Perform the upkeep by releasing funds for ready invoices
     * @param performData Encoded invoice IDs to release
     */
    function performUpkeep(bytes calldata performData) external override {
        // Decode the invoice IDs
        bytes32[] memory invoicesToRelease = abi.decode(performData, (bytes32[]));

        // Release funds for each invoice
        for (uint256 i = 0; i < invoicesToRelease.length; i++) {
            bytes32 invoiceId = invoicesToRelease[i];
            Invoice storage invoice = invoices[invoiceId];

            // Double-check the conditions before releasing
            if (!invoice.released && 
                !invoice.refunded && 
                invoice.totalContributed >= invoice.invoiceAmount &&
                block.timestamp <= invoice.deadline) {
                _releaseInvoice(invoiceId);
                emit AutomationTriggered(invoiceId, block.timestamp);
            }
        }
    }

    // View functions
    function getInvoice(bytes32 invoiceId) external view returns (
        address seller,
        uint256 invoiceAmount,
        address token,
        uint256 deadline,
        uint256 totalContributed,
        bool released,
        bool refunded
    ) {
        Invoice storage invoice = invoices[invoiceId];
        return (
            invoice.seller,
            invoice.invoiceAmount,
            invoice.token,
            invoice.deadline,
            invoice.totalContributed,
            invoice.released,
            invoice.refunded
        );
    }

    function getContribution(bytes32 invoiceId, address contributor) external view returns (uint256) {
        return invoices[invoiceId].contributions[contributor];
    }

    function getTotalInvoiceCount() external view returns (uint256) {
        return allInvoiceIds.length;
    }

    function getInvoiceIdAtIndex(uint256 index) external view returns (bytes32) {
        require(index < allInvoiceIds.length, "Index out of bounds");
        return allInvoiceIds[index];
    }

    // Admin functions
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}

