// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title SplitBilling  
 * @dev Enhanced contract for splitting payments with deadline management
 * @notice Allows payment splitting among recipients with deadline tracking and failure isolation
 */
contract SplitBilling is ReentrancyGuard {

    // Constants
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant MAX_RECIPIENTS = 20;
    uint256 public constant GAS_LIMIT = 50000;
    address public constant ETH_TOKEN = address(0);

    // Structs
    struct SplitRequest {
        address payer;
        address token;
        uint256 totalAmount;
        uint256 deadline;
        bool completed;
        uint256 successCount;
        uint256 failCount;
        address[] recipients;
        uint256[] shares;
        mapping(address => bool) hasPaid;
        mapping(address => uint256) expectedAmounts;
    }

    // Storage
    mapping(address => mapping(address => uint256)) public failedTransfers; // recipient => token => amount
    mapping(uint256 => SplitRequest) public splitRequests;
    uint256 public splitCounter;

    // Events
    event SplitCreated(
        uint256 indexed splitId,
        address indexed payer,
        address token,
        uint256 totalAmount,
        uint256 deadline,
        address[] recipients,
        uint256[] shares
    );

    event SplitExecuted(
        uint256 indexed splitId,
        address indexed payer,
        address token,
        uint256 totalAmount,
        uint256 successCount,
        uint256 failCount
    );

    event TransferFailed(
        uint256 indexed splitId,
        address indexed recipient,
        address token,
        uint256 amount
    );

    event FailedTransferClaimed(
        address indexed recipient,
        address token,
        uint256 amount
    );

    event UnpaidRecipientAlert(
        uint256 indexed splitId,
        address indexed recipient,
        uint256 deadline
    );

    event RecipientPaid(
        uint256 indexed splitId,
        address indexed recipient,
        address token,
        uint256 amount
    );

    // Custom errors
    error InvalidInput();
    error ClaimTransferFailed();
    error SplitExpired();
    error SplitNotFound();
    error AlreadyPaid();
    error InvalidAmount();

    /**
     * @dev Create a split payment request with deadline
     * @param recipients Array of recipient addresses (max 20)
     * @param shares Array of shares in basis points (must sum to 10000)
     * @param token Token address (address(0) for ETH)
     * @param deadline Unix timestamp deadline
     */
    function createSplit(
        address[] calldata recipients,
        uint256[] calldata shares,
        address token,
        uint256 deadline
    ) external payable nonReentrant returns (uint256 splitId) {
        // Input validation
        if (recipients.length == 0 || recipients.length > MAX_RECIPIENTS) revert InvalidInput();
        if (recipients.length != shares.length) revert InvalidInput();
        if (deadline <= block.timestamp) revert InvalidInput();

        uint256 totalAmount = token == ETH_TOKEN ? msg.value : 0;
        if (totalAmount == 0 && token == ETH_TOKEN) revert InvalidInput();

        uint256 totalShares = 0;
        for (uint256 i = 0; i < shares.length; i++) {
            if (recipients[i] == address(0) || shares[i] == 0) revert InvalidInput();
            totalShares += shares[i];
        }
        if (totalShares != BASIS_POINTS) revert InvalidInput();

        // Handle ERC20 transfer to contract
        if (token != ETH_TOKEN) {
            totalAmount = _getERC20Amount(token);
            IERC20(token).transferFrom(msg.sender, address(this), totalAmount);
        }

        // Create split request
        splitId = ++splitCounter;
        SplitRequest storage split = splitRequests[splitId];
        split.payer = msg.sender;
        split.token = token;
        split.totalAmount = totalAmount;
        split.deadline = deadline;
        split.recipients = recipients;
        split.shares = shares;

        // Calculate expected amounts for each recipient
        for (uint256 i = 0; i < recipients.length; i++) {
            split.expectedAmounts[recipients[i]] = (totalAmount * shares[i]) / BASIS_POINTS;
        }

        emit SplitCreated(splitId, msg.sender, token, totalAmount, deadline, recipients, shares);

        // Execute split immediately
        _executeSplit(splitId);
    }

    /**
     * @dev Execute split with failure isolation - can be called by anyone after creation
     * @param splitId The ID of the split to execute
     */
    function executeSplit(uint256 splitId) external nonReentrant {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();
        if (splitRequests[splitId].completed) revert AlreadyPaid();
        if (block.timestamp > splitRequests[splitId].deadline) revert SplitExpired();

        _executeSplit(splitId);
    }

    /**
     * @dev Check for unpaid recipients after deadline and emit alerts
     * @param splitId The ID of the split to check
     */
    function checkUnpaidRecipients(uint256 splitId) external {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();

        SplitRequest storage split = splitRequests[splitId];
        if (block.timestamp <= split.deadline) revert InvalidInput();

        for (uint256 i = 0; i < split.recipients.length; i++) {
            address recipient = split.recipients[i];
            if (!split.hasPaid[recipient]) {
                emit UnpaidRecipientAlert(splitId, recipient, split.deadline);
            }
        }
    }

    /**
     * @dev Claim failed transfers
     * @param token Token to claim (address(0) for ETH)
     */
    function claimFailedTransfer(address token) external nonReentrant {
        uint256 amount = failedTransfers[msg.sender][token];
        if (amount == 0) revert InvalidInput();

        failedTransfers[msg.sender][token] = 0;

        bool success;
        if (token == ETH_TOKEN) {
            (success, ) = msg.sender.call{value: amount, gas: GAS_LIMIT}("");
        } else {
            try IERC20(token).transfer(msg.sender, amount) {
                success = true;
            } catch {
                success = false;
            }
        }

        if (success) {
            emit FailedTransferClaimed(msg.sender, token, amount);
        } else {
            failedTransfers[msg.sender][token] = amount; // Restore balance
            revert ClaimTransferFailed();
        }
    }

    /**
     * @dev Execute split with individual failure isolation
     */
    function _executeSplit(uint256 splitId) internal {
        SplitRequest storage split = splitRequests[splitId];

        uint256 successCount = 0;
        uint256 failCount = 0;
        uint256 remaining = split.totalAmount;

        for (uint256 i = 0; i < split.recipients.length; i++) {
            address recipient = split.recipients[i];
            uint256 amount = (i == split.recipients.length - 1) 
                ? remaining 
                : split.expectedAmounts[recipient];

            if (i < split.recipients.length - 1) remaining -= amount;

            if (_attemptTransfer(recipient, split.token, amount)) {
                successCount++;
                split.hasPaid[recipient] = true;
                emit RecipientPaid(splitId, recipient, split.token, amount);
            } else {
                failCount++;
                failedTransfers[recipient][split.token] += amount;
                emit TransferFailed(splitId, recipient, split.token, amount);
            }
        }

        split.successCount = successCount;
        split.failCount = failCount;
        split.completed = true;

        emit SplitExecuted(splitId, split.payer, split.token, split.totalAmount, successCount, failCount);
    }

    /**
     * @dev Attempt transfer with gas limit protection
     */
    function _attemptTransfer(
        address recipient,
        address token,
        uint256 amount
    ) internal returns (bool success) {
        if (token == ETH_TOKEN) {
            (success, ) = recipient.call{value: amount, gas: GAS_LIMIT}("");
        } else {
            try IERC20(token).transfer(recipient, amount) {
                success = true;
            } catch {
                success = false;
            }
        }
    }

    /**
     * @dev Get ERC20 transfer amount from allowance
     */
    function _getERC20Amount(address token) internal view returns (uint256) {
        return IERC20(token).allowance(msg.sender, address(this));
    }

    /**
     * @dev Get failed transfer amount
     */
    function getFailedAmount(address recipient, address token) external view returns (uint256) {
        return failedTransfers[recipient][token];
    }

    /**
     * @dev Get split request details
     */
    function getSplitDetails(uint256 splitId) external view returns (
        address payer,
        address token,
        uint256 totalAmount,
        uint256 deadline,
        bool completed,
        uint256 successCount,
        uint256 failCount,
        address[] memory recipients,
        uint256[] memory shares
    ) {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();

        SplitRequest storage split = splitRequests[splitId];
        return (
            split.payer,
            split.token,
            split.totalAmount,
            split.deadline,
            split.completed,
            split.successCount,
            split.failCount,
            split.recipients,
            split.shares
        );
    }

    /**
     * @dev Check if recipient has been paid in a split
     */
    function hasRecipientPaid(uint256 splitId, address recipient) external view returns (bool) {
        if (splitId == 0 || splitId > splitCounter) revert SplitNotFound();
        return splitRequests[splitId].hasPaid[recipient];
    }

    // Accept ETH
    receive() external payable {}
}

